import { Page, APIRequestContext } from '@playwright/test';

/**
 * WordPress API helpers for E2E testing
 */
export class WordPressHelper {
  constructor(
    private page: Page,
    private request: APIRequestContext
  ) {}

  /**
   * Create a test JWT token via WordPress API
   */
  async createTestToken(username: string = 'admin', password: string = 'password') {
    const response = await this.request.post('/wp-json/jwt-auth/v1/token', {
      data: {
        username,
        password
      }
    });

    if (!response.ok()) {
      throw new Error(`Failed to create test token: ${response.status()}`);
    }

    return response.json();
  }

  /**
   * Get nonce for authenticated requests
   */
  async getNonce(): Promise<string> {
    // Extract nonce from the page if available
    const nonce = await this.page.evaluate(() => {
      return (window as any).jwtAuthPro?.nonce || 
             (window as any).wpApiSettings?.nonce;
    });

    if (!nonce) {
      throw new Error('WordPress nonce not found');
    }

    return nonce;
  }

  /**
   * Wait for WordPress admin page to load
   */
  async waitForAdminPage() {
    await this.page.waitForSelector('#wpadminbar');
    await this.page.waitForLoadState('networkidle');
  }

  /**
   * Wait for React app to initialize
   */
  async waitForReactApp() {
    // Wait for the main app container
    await this.page.waitForSelector('#jwt-auth-pro-app');

    // Wait for navigation or dashboard content to load
    await this.page.waitForSelector(
      '[data-testid="dashboard-tab"], nav a:has-text("Dashboard"), h1:has-text("Dashboard")',
      { timeout: 15000 }
    );
  }

  /**
   * Navigate between JWT Auth Pro admin tabs
   */
  async navigateToTab(tabName: 'dashboard' | 'tokens' | 'settings' | 'system') {
    const selectors = {
      dashboard: '[data-testid="dashboard-tab"], nav a:has-text("Dashboard")',
      tokens: '[data-testid="tokens-tab"], nav a:has-text("Tokens")',
      settings: '[data-testid="settings-tab"], nav a:has-text("Settings")',
      system: '[data-testid="system-tab"], nav a:has-text("System")'
    };

    const selector = selectors[tabName];
    await this.page.click(selector);
    await this.page.waitForLoadState('networkidle');
  }

  /**
   * Clean up test data
   */
  async cleanupTestData() {
    try {
      // This would call a cleanup endpoint if available
      await this.request.post('/wp-json/jwt-auth-pro/v1/test/cleanup');
    } catch (error) {
      // Cleanup is best-effort, don't fail tests if it doesn't work
      console.warn('Test cleanup failed:', error);
    }
  }
}

