import { test, expect } from '../fixtures/admin-user';
import { WordPressHelper } from '../helpers/wordpress';

test.describe('JWT Auth Pro - Core Functionality @core', () => {
  test('Plugin loads and React app works', async ({ jwtAdminPage }) => {
    // Verify we're on the right page
    expect(jwtAdminPage.url()).toContain('jwt-auth-pro');

    // Verify React app container exists and has content
    const app = jwtAdminPage.locator('#jwt-auth-pro-app');
    await expect(app).toBeVisible();

    const hasContent = await app.locator('*').count();
    expect(hasContent).toBeGreaterThan(0);

    console.log('✅ React app loaded successfully');
  });

  test('Dashboard displays correctly', async ({ jwtAdminPage }) => {
    // Look for dashboard content
    const dashboardTitle = jwtAdminPage.locator('text=Dashboard Overview');
    await expect(dashboardTitle).toBeVisible();

    console.log('✅ Dashboard title displays');
  });

  test('Charts render successfully', async ({ jwtAdminPage }) => {
    // Wait a moment for charts to render
    await jwtAdminPage.waitForTimeout(2000);

    // Look for any SVG elements (likely charts)
    const svgElements = jwtAdminPage.locator('svg');
    const svgCount = await svgElements.count();

    expect(svgCount).toBeGreaterThan(0);
    console.log(`✅ Found ${svgCount} SVG elements (charts)`);
  });

  test('Page is responsive', async ({ jwtAdminPage }) => {
    const app = jwtAdminPage.locator('#jwt-auth-pro-app');

    // Test mobile size
    await jwtAdminPage.setViewportSize({ width: 375, height: 667 });
    await jwtAdminPage.waitForTimeout(500);
    await expect(app).toBeVisible();

    // Test tablet size  
    await jwtAdminPage.setViewportSize({ width: 768, height: 1024 });
    await jwtAdminPage.waitForTimeout(500);
    await expect(app).toBeVisible();

    // Back to desktop
    await jwtAdminPage.setViewportSize({ width: 1200, height: 800 });
    await expect(app).toBeVisible();

    console.log('✅ Responsive design works');
  });

  test('No critical JavaScript errors', async ({ jwtAdminPage }) => {
    const errors: string[] = [];

    jwtAdminPage.on('console', msg => {
      if (msg.type() === 'error') {
        errors.push(msg.text());
      }
    });

    // Wait a bit to catch any errors
    await jwtAdminPage.waitForTimeout(3000);

    // Filter out irrelevant errors
    const criticalErrors = errors.filter(error => 
      !error.includes('favicon') &&
      !error.includes('Extension') &&
      !error.includes('chrome-extension')
    );

    expect(criticalErrors.length).toBe(0);

    if (criticalErrors.length === 0) {
      console.log('✅ No critical JavaScript errors');
    } else {
      console.log('❌ Found errors:', criticalErrors);
    }
  });

  test('WordPress integration is solid', async ({ adminPage }) => {
    // Verify plugin appears in admin menu
    const jwtMenu = adminPage.locator('#adminmenu a:has-text("JWT")');

    if (await jwtMenu.count() > 0) {
      await expect(jwtMenu).toBeVisible();
      console.log('✅ Plugin menu integration works');
    }

    // Verify we can access the plugin page
    await adminPage.goto('/wp-admin/admin.php?page=jwt-auth-pro');

    const title = await adminPage.title();
    expect(title).toContain('JWT Auth Pro');

    console.log('✅ Direct page access works');
  });

  test('Page loads in reasonable time', async ({ jwtAdminPage }) => {
    const startTime = Date.now();

    // Reload the page
    await jwtAdminPage.reload();

    // Wait for React app to be ready
    await jwtAdminPage.waitForSelector('#jwt-auth-pro-app');
    await jwtAdminPage.waitForTimeout(1000); // Give React time to render

    const loadTime = Date.now() - startTime;

    // Should load within 10 seconds
    expect(loadTime).toBeLessThan(10000);

    console.log(`✅ Page loaded in ${loadTime}ms`);
  });
});

