import { chromium } from 'playwright';

(async () => {
  const browser = await chromium.launch({ headless: false });
  const page = await browser.newPage();

  try {
    console.log('Navigating to wp-admin...');
    await page.goto('http://jwt.test/wp-admin');

    console.log('Page loaded, checking for login form...');

    // Check if we're already logged in or need to login
    const needsLogin = await page.locator('#user_login').count() > 0;

    if (needsLogin) {
      console.log('Login form found. Trying admin/password...');

      await page.fill('#user_login', 'admin');
      await page.fill('#user_pass', 'password');
      await page.click('#wp-submit');

      // Wait to see what happens
      await page.waitForTimeout(3000);

      // Check if we're logged in now
      const isLoggedIn = await page.locator('#wpadminbar').count() > 0;
      const currentUrl = page.url();

      console.log('Current URL after login attempt:', currentUrl);
      console.log('Is logged in (has admin bar):', isLoggedIn);

      if (!isLoggedIn) {
        // Check for error messages
        const errorMessage = await page.locator('.login_error').textContent();
        if (errorMessage) {
          console.log('Login error:', errorMessage);
        }

        // Check what the page title is
        const title = await page.title();
        console.log('Page title:', title);
      }

    } else {
      console.log('Already logged in or no login form found');
      const title = await page.title();
      console.log('Page title:', title);
    }

  } catch (error) {
    console.error('Error:', error.message);
  }

  console.log('Press any key to continue...');
  await page.waitForTimeout(10000); // Give time to see what's happening

  await browser.close();
})();

