import { test as base, expect } from '@playwright/test';

// WordPress admin credentials for testing
const ADMIN_CREDENTIALS = {
  username: 'admin',
  password: 'password'
};

// Extend test with WordPress admin fixtures
export const test = base.extend({
  // Custom fixture for WordPress admin login
  adminPage: async ({ page }, use) => {
    // Navigate to WordPress admin login
    await page.goto('/wp-admin');

    // Fill login form
    await page.fill('#user_login', ADMIN_CREDENTIALS.username);
    await page.fill('#user_pass', ADMIN_CREDENTIALS.password);
    await page.click('#wp-submit');

    // Wait for successful login - could redirect to different admin pages
    await page.waitForURL('**/wp-admin/**');

    // Verify we're actually logged in by checking for admin bar
    await page.waitForSelector('#wpadminbar', { timeout: 10000 });

    await use(page);
  },

  // Custom fixture for JWT Auth Pro admin page
  jwtAdminPage: async ({ adminPage }, use) => {
    // Navigate to JWT Auth Pro admin page
    await adminPage.goto('/wp-admin/admin.php?page=jwt-auth-pro');

    // Wait for the page to load (don't assume React app is there yet)
    await adminPage.waitForLoadState('networkidle');

    await use(adminPage);
  }
});

export { expect };

