import { chromium } from 'playwright';

(async () => {
  const browser = await chromium.launch({ headless: false });
  const page = await browser.newPage();

  try {
    console.log('Logging in to jwt.test...');
    await page.goto('http://jwt.test/wp-admin');

    await page.fill('#user_login', 'tmeister');
    await page.fill('#user_pass', 'poipoipoi');
    await page.click('#wp-submit');

    await page.waitForURL('**/wp-admin/**');
    console.log('✅ Logged in successfully');

    console.log('Navigating to JWT Auth Pro page...');
    await page.goto('http://jwt.test/wp-admin/admin.php?page=jwt-auth-pro');
    await page.waitForLoadState('networkidle');

    console.log('Page loaded. Checking for React app...');

    const appExists = await page.locator('#jwt-auth-pro-app').count() > 0;
    console.log('App container exists:', appExists);

    if (appExists) {
      const appContent = await page.locator('#jwt-auth-pro-app').innerHTML();
      console.log('App container content:');
      console.log(appContent);

      const hasChildren = await page.locator('#jwt-auth-pro-app *').count();
      console.log('Number of child elements:', hasChildren);
    }

    // Check for any JavaScript errors in console
    console.log('\nChecking console for errors...');
    const messages = [];
    page.on('console', msg => {
      if (msg.type() === 'error') {
        messages.push(msg.text());
      }
    });

    await page.waitForTimeout(3000);

    if (messages.length > 0) {
      console.log('Console errors found:');
      messages.forEach(msg => console.log(' -', msg));
    } else {
      console.log('No console errors');
    }

    // Check page title
    const title = await page.title();
    console.log('Page title:', title);

    // Check if we can see any content at all
    const bodyText = await page.locator('body').innerText();
    if (bodyText.includes('JWT')) {
      console.log('✅ JWT content found in page');
    } else {
      console.log('❌ No JWT content found');
    }

  } catch (error) {
    console.error('Error:', error.message);
  }

  console.log('\nLeaving browser open for 10 seconds to inspect...');
  await page.waitForTimeout(10000);

  await browser.close();
})();

