        const hasSuccess = await jwtAdminPage.locator('.success, .saved').count() > 0;
        if (hasSuccess) {
          console.log('Rate limit setting appeared to save');
        }
      }
    } else {
      console.log('No rate limiting settings found');
    }
  });

  test('Settings persist after page reload', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await helper.waitForReactApp();
    await helper.navigateToTab('settings');

    // Find any input field that we can modify
    const testableFields = [
      'input[type="text"]',
      'input[type="number"]',
      'select',
      'textarea'
    ];

    let testField = null;
    for (const selector of testableFields) {
      const field = jwtAdminPage.locator(selector);
      if (await field.count() > 0) {
        testField = field.first();
        break;
      }
    }

    if (testField) {
      // Get original value
      const originalValue = await testField.inputValue();

