      const testValue = 'test-value-123';
      await testField.fill(testValue);

      // Save if there's a save button
      const saveButton = jwtAdminPage.locator('button:has-text("Save")');
      if (await saveButton.count() > 0) {
        await saveButton.click();
        await jwtAdminPage.waitForTimeout(1000);

        // Reload page
        await jwtAdminPage.reload();
        await helper.waitForReactApp();
        await helper.navigateToTab('settings');

        // Check if value persisted
        const persistedValue = await testField.inputValue();
        if (persistedValue === testValue) {
          console.log('Settings persistence verified');
