          console.log('No validation errors found - may not be implemented yet');
        }
      }
    } else {
      console.log('No numeric inputs found for validation testing');
    }
  });

  test.afterEach(async ({ jwtAdminPage }) => {
    // Clean up any test data
    if (testData) {
      await testData.cleanup();
    }
  });
});

