import { test, expect } from '../fixtures/admin-user';
import { WordPressHelper } from '../helpers/wordpress';
import { TestDataManager } from '../fixtures/test-data';
import { JWTAssertions } from '../helpers/assertions';

test.describe('JWT Auth Pro - Settings Management', () => {
  let helper: WordPressHelper;
  let testData: TestDataManager;
  let assertions: JWTAssertions;

  test.beforeEach(async ({ jwtAdminPage }) => {
    helper = new WordPressHelper(jwtAdminPage, jwtAdminPage.request);
    testData = new TestDataManager(jwtAdminPage, jwtAdminPage.request);
    assertions = new JWTAssertions(jwtAdminPage);
  });

  test('Settings page loads', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await helper.waitForReactApp();

    // Navigate to settings
    await helper.navigateToTab('settings');

    // Look for settings form or configuration options
    const settingsSelectors = [
      'form',
      '.settings-form',
      '[data-testid*="settings"]',
      'input[type="text"]',
      'select',
      'input[type="checkbox"]'
    ];

    let hasSettings = false;
    for (const selector of settingsSelectors) {
      if (await jwtAdminPage.locator(selector).count() > 0) {
        hasSettings = true;
        await expect(jwtAdminPage.locator(selector).first()).toBeVisible();
        break;
      }
    }

    if (!hasSettings) {
      console.log('No settings form found - feature may not be implemented');
    }
  });

  test('JWT expiration setting works (if available)', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await helper.waitForReactApp();
    await helper.navigateToTab('settings');

    // Look for JWT expiration setting
    const expirationSelectors = [
      'select[data-testid*="expiration"]',
      'input[data-testid*="expiration"]',
      'select:has(option:has-text("day"))',
      'select:has(option:has-text("hour"))',
      'input[placeholder*="expiration"]'
    ];

    let expirationField = null;
    for (const selector of expirationSelectors) {
      const field = jwtAdminPage.locator(selector);
      if (await field.count() > 0) {
        expirationField = field;
        break;
      }
    }

    if (expirationField) {
      // Get current value
      const currentValue = await expirationField.inputValue();

      // Try to change the value
      if (await expirationField.getAttribute('tag') === 'select') {
        const options = expirationField.locator('option');
        if (await options.count() > 1) {
          await expirationField.selectOption({ index: 1 });
        }
      } else {
        await expirationField.fill('24');
      }

      // Look for save button
      const saveButton = jwtAdminPage.locator(
        'button:has-text("Save"), input[type="submit"], [data-testid*="save"]'
      );

      if (await saveButton.count() > 0) {
        await saveButton.click();

        // Look for success message
        await jwtAdminPage.waitForTimeout(1000);
        const hasSuccess = await jwtAdminPage.locator(
          '.success, .saved, .toast, [data-testid*="success"]'
        ).count() > 0;

        if (hasSuccess) {
          console.log('Settings save appeared to work');
        }
      }
    } else {
      console.log('No JWT expiration setting found');
    }
  });

  test('Security settings toggle (if available)', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await helper.waitForReactApp();
    await helper.navigateToTab('settings');

    // Look for security-related checkboxes or toggles
    const securitySelectors = [
      'input[type="checkbox"][data-testid*="cors"]',
      'input[type="checkbox"][data-testid*="security"]',
      'input[type="checkbox"][data-testid*="enable"]',
      'label:has-text("CORS")',
      'label:has-text("Security")'
    ];

    let securitySetting = null;
    for (const selector of securitySelectors) {
      const setting = jwtAdminPage.locator(selector);
      if (await setting.count() > 0) {
        securitySetting = setting;
        break;
      }
    }

    if (securitySetting) {
      // Check current state
      const isChecked = await securitySetting.isChecked();

      // Toggle the setting
      await securitySetting.click();
      await jwtAdminPage.waitForTimeout(500);

      // Verify it changed
      const newState = await securitySetting.isChecked();
      expect(newState).toBe(!isChecked);

      // Look for auto-save or save button
      const saveButton = jwtAdminPage.locator('button:has-text("Save")');
      if (await saveButton.count() > 0) {
        await saveButton.click();
        await jwtAdminPage.waitForTimeout(1000);
      }
    } else {
      console.log('No security settings found');
    }
  });

  test('Rate limiting configuration (if available)', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await helper.waitForReactApp();
    await helper.navigateToTab('settings');

    // Look for rate limiting settings
    const rateLimitSelectors = [
      'input[data-testid*="rate"]',
      'input[data-testid*="limit"]',
      'input[placeholder*="rate"]',
      'input[placeholder*="limit"]',
      'select[data-testid*="rate"]'
    ];

    let rateLimitField = null;
    for (const selector of rateLimitSelectors) {
      const field = jwtAdminPage.locator(selector);
      if (await field.count() > 0) {
        rateLimitField = field;
        break;
      }
    }

    if (rateLimitField) {
      // Try to modify rate limit
      await rateLimitField.fill('100');
      await jwtAdminPage.waitForTimeout(500);

      // Look for save mechanism
      const saveButton = jwtAdminPage.locator('button:has-text("Save")');
      if (await saveButton.count() > 0) {
        await saveButton.click();
        await jwtAdminPage.waitForTimeout(1000);

        // Check for success indicator
        const hasSuccess = await jwtAdminPage.locator('.success, .saved').count() > 0;
        if (hasSuccess) {
          console.log('Rate limit setting appeared to save');
        }
      }
    } else {
      console.log('No rate limiting settings found');
    }
  });

  test('Settings persist after page reload', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await helper.waitForReactApp();
    await helper.navigateToTab('settings');

    // Find any input field that we can modify
    const testableFields = [
      'input[type="text"]',
      'input[type="number"]',
      'select',
      'textarea'
    ];

    let testField = null;
    for (const selector of testableFields) {
      const field = jwtAdminPage.locator(selector);
      if (await field.count() > 0) {
        testField = field.first();
        break;
      }
    }

    if (testField) {
      // Get original value
      const originalValue = await testField.inputValue();

      // Set a test value
      const testValue = 'test-value-123';
      await testField.fill(testValue);

      // Save if there's a save button
      const saveButton = jwtAdminPage.locator('button:has-text("Save")');
      if (await saveButton.count() > 0) {
        await saveButton.click();
        await jwtAdminPage.waitForTimeout(1000);

        // Reload page
        await jwtAdminPage.reload();
        await helper.waitForReactApp();
        await helper.navigateToTab('settings');

        // Re-find the field after reload
        let reloadedField = null;
        for (const selector of testableFields) {
          const field = jwtAdminPage.locator(selector);
          if (await field.count() > 0) {
            reloadedField = field.first();
            break;
          }
        }

        // Check if value persisted
        const persistedValue = reloadedField ? await reloadedField.inputValue() : null;
        if (persistedValue === testValue) {
          console.log('Settings persistence verified');
        } else {
          console.log('Settings may not persist or auto-save may not be implemented');
        }

        // Restore original value
        if (reloadedField) {
          await reloadedField.fill(originalValue);
        }
        if (await saveButton.count() > 0) {
          await saveButton.click();
        }
      }
    } else {
      console.log('No testable settings fields found');
    }
  });

  test('Invalid settings show validation errors', async ({ jwtAdminPage }) => {
    const appContainer = jwtAdminPage.locator('#jwt-auth-pro-app');
    const hasApp = await appContainer.count() > 0;

    if (!hasApp) {
      test.skip('JWT Auth Pro plugin not installed or activated');
    }

    await helper.waitForReactApp();
    await helper.navigateToTab('settings');

    // Find a numeric input to test with invalid data
    const numericInputs = jwtAdminPage.locator('input[type="number"], input[data-testid*="expiration"]');

    if (await numericInputs.count() > 0) {
      const field = numericInputs.first();

      // Enter invalid data
      await field.fill('-1');
      await jwtAdminPage.waitForTimeout(500);

      // Try to save
      const saveButton = jwtAdminPage.locator('button:has-text("Save")');
      if (await saveButton.count() > 0) {
        await saveButton.click();
        await jwtAdminPage.waitForTimeout(1000);

        // Look for error messages
        const errorSelectors = [
          '.error',
          '.invalid',
          '.validation-error',
          '[data-testid*="error"]',
          '.field-error'
        ];

        let hasError = false;
        for (const selector of errorSelectors) {
          if (await jwtAdminPage.locator(selector).count() > 0) {
            hasError = true;
            console.log('Validation error displayed');
            break;
          }
        }

        if (!hasError) {
          console.log('No validation errors found - may not be implemented yet');
        }
      }
    } else {
      console.log('No numeric inputs found for validation testing');
    }
  });

  test.afterEach(async ({ jwtAdminPage }) => {
    // Clean up any test data
    if (testData) {
      await testData.cleanup();
    }
  });
});

