     1→<?php
     2→
     3→declare(strict_types=1);
     4→
     5→/**
     6→ * Plugin Name: JWT Auth Pro
     7→ * Plugin URI: https://github.com/tmeister/jwt-auth-pro
     8→ * Description: Professional JWT Authentication for WordPress REST API
     9→ * Version: 0.1.0
    10→ * Author: Tmeister
    11→ * Author URI: https://enriquechavez.co
    12→ * License: GPL v2 or later
    13→ * License URI: https://www.gnu.org/licenses/gpl-2.0.html
    14→ * Text Domain: jwt-auth-pro
    15→ * Domain Path: /languages
    16→ * Requires PHP: 8.1
    17→ */
    18→
    19→use Tmeister\JWTAuthPro\JWTAuthPro;
    20→
    21→use function Tmeister\JWTAuthPro\activate;
    22→use function Tmeister\JWTAuthPro\deactivate;
    23→
    24→if (!defined('ABSPATH')) {
    25→    exit;
    26→}
    27→
    28→// Define plugin constants
    29→define('JWT_AUTH_PRO_FILE', __FILE__);
    30→define('JWT_AUTH_PRO_PATH', plugin_dir_path(__FILE__));
    31→define('JWT_AUTH_PRO_URL', plugin_dir_url(__FILE__));
    32→define('JWT_AUTH_PRO_VERSION', '0.1.0');
    33→define('JWT_AUTH_PRO_BASENAME', plugin_basename(__FILE__));
    34→
    35→// Define database table constants
    36→define('JWT_AUTH_PRO_TOKENS_TABLE', 'jwt_auth_tokens');
    37→define('JWT_AUTH_PRO_ANALYTICS_TABLE', 'jwt_auth_analytics');
    38→define('JWT_AUTH_PRO_ANALYTICS_SUMMARY_TABLE', 'jwt_auth_analytics_summary');
    39→
    40→// Composer autoloader
    41→if (file_exists($composer = __DIR__ . '/vendor/autoload.php')) {
    42→    require_once $composer;
    43→}
    44→
    45→// Verify PHP version
    46→if (version_compare(PHP_VERSION, '8.1.0', '<')) {
    47→    add_action('admin_notices', function () {
    48→        echo '<div class="notice notice-error"><p>' . __(
    49→            'JWT Auth Pro requires PHP 8.1 or higher.',
    50→            'jwt-auth-pro'
    51→        ) . '</p></div>';
    52→    });
    53→
    54→    return;
    55→}
    56→
    57→// Register activation hook
    58→register_activation_hook(
    59→    JWT_AUTH_PRO_FILE,
    60→    static function (): void {
    61→        require_once JWT_AUTH_PRO_PATH . 'src/activation.php';
    62→        activate();
    63→    }
    64→);
    65→
    66→// Register deactivation hook
    67→register_deactivation_hook(
    68→    JWT_AUTH_PRO_FILE,
    69→    static function (): void {
    70→        require_once JWT_AUTH_PRO_PATH . 'src/deactivation.php';
    71→        deactivate();
    72→    }
    73→);
    74→
    75→// Initialize plugin
    76→add_action(
    77→    'plugins_loaded',
    78→    static function (): void {
    79→        require_once JWT_AUTH_PRO_PATH . 'src/JWTAuthPro.php';
    80→        JWTAuthPro::getInstance();
    81→    }
    82→);
    83→
    84→function run_jwt_auth_pro(): void
    85→{
    86→    $plugin = JWTAuthPro::getInstance();
    87→    $plugin->boot();
    88→}
    89→
    90→run_jwt_auth_pro();
    91→

