import { useEffect, useState, useMemo } from 'react';
import { Header } from '@/components/ui/header';
import { PageWrapper } from '@/components/ui/page-wrapper';
import { analyticsAPI } from '@/services/api';
import type { TokenStats } from '@/types/metrics';
import type { HistoricalData } from '@/types/analytics';
import { TokenMetricsCard } from '@/components/tokens/token-metrics-card';
import { Key, Users, Rocket, BookOpen, LifeBuoy, ArrowRight } from 'lucide-react';
import { getEventMetrics, getTokenMetrics, getTotalsMetrics } from '@/lib/metrics';
import { Card, CardContent, CardHeader, CardTitle, CardFooter } from '@/components/ui/card';
import { AnalyticsCharts } from '@/components/analytics/analytics-charts';

interface QuickLinkProps {
  title: string;
  description: string;
  icon: React.ComponentType<{ className?: string }>;
  href: string;
  linkText: string;
}

function QuickLink({ title, description, icon: Icon, href, linkText }: QuickLinkProps) {
  return (
    <Card>
      <CardHeader>
        <div className="jwt-flex jwt-items-center jwt-space-x-2">
          <div className="jwt-p-2 jwt-bg-primary/10 jwt-rounded-full">
            <Icon className="jwt-h-4 jwt-w-4 jwt-text-primary" />
          </div>
          <CardTitle className="jwt-text-base jwt-font-medium">{title}</CardTitle>
        </div>
      </CardHeader>
      <CardContent>
        <p className="jwt-text-sm jwt-text-muted-foreground">{description}</p>
      </CardContent>
      <CardFooter>
        <a
          href={href}
          className="jwt-flex jwt-items-center jwt-text-sm jwt-text-primary hover:jwt-text-primary/80"
        >
          {linkText} <ArrowRight className="jwt-h-4 jwt-w-4 jwt-ml-1" />
        </a>
      </CardFooter>
    </Card>
  );
}

function MetricsSection({
  isLoading,
  allTimeStats
}: {
  isLoading: boolean;
  allTimeStats: TokenStats | null;
}) {
  const metrics = useMemo(() => {
    const tokenMetrics = getTokenMetrics(allTimeStats);
    const eventMetrics = getEventMetrics(allTimeStats);
    const totalsMetrics = getTotalsMetrics(allTimeStats);

    return {
      tokenMetrics,
      eventMetrics,
      totalsMetrics
    };
  }, [allTimeStats]);

  return (
    <div className="jwt-grid jwt-gap-4 jwt-grid-cols-1 sm:jwt-grid-cols-2">
      <TokenMetricsCard
        isLoading={isLoading}
        title="Total Tokens All Time"
        icon={Key}
        mainMetric={metrics.tokenMetrics.mainMetric}
        metrics={metrics.tokenMetrics.metrics}
        gridCols={3}
        totalsMetrics={metrics.totalsMetrics[0].totals}
      />

      <TokenMetricsCard
        isLoading={isLoading}
        title="Token Events All Time"
        icon={Users}
        mainMetric={metrics.eventMetrics.mainMetric}
        metrics={metrics.eventMetrics.metrics}
        gridCols={2}
        totalsMetrics={metrics.totalsMetrics[1].totals}
      />
    </div>
  );
}

const quickLinks = [
  {
    title: 'Quick Start',
    description: 'Get start guide to help you get started with JWT Auth Pro.',
    icon: Rocket,
    href: 'https://docs.jwtauth.pro/introduction/quickstart?utm_source=jwt-auth-pro&utm_medium=admin-dashboard&utm_campaign=quickstart-guide',
    linkText: 'Get Started'
  },
  {
    title: 'Documentation',
    description: 'All the documentation you need to get started with JWT Auth Pro.',
    icon: BookOpen,
    href: 'https://docs.jwtauth.pro/?utm_source=jwt-auth-pro&utm_medium=admin-dashboard&utm_campaign=documentation',
    linkText: 'View Documentation'
  },
  {
    title: 'Support',
    description: 'Need help? Contact support for any issues you may have.',
    icon: LifeBuoy,
    href: 'https://jwtauth.freshdesk.com/support/tickets/new?utm_source=jwt-auth-pro&utm_medium=admin-dashboard&utm_campaign=support-ticket',
    linkText: 'Create a Ticket'
  }
] as const;

export default function HomePage() {
  const [isLoading, setIsLoading] = useState(true);
  const [allTimeStats, setAllTimeStats] = useState<TokenStats | null>(null);
  const [historicalData, setHistoricalData] = useState<HistoricalData[]>([]);
  const [period, setPeriod] = useState<'7d' | '30d' | '90d'>('7d');
  const isWhitelabeled = window.wpApiSettings.is_whitelabeled;

  useEffect(() => {
    const controller = new AbortController();

    async function fetchData() {
      try {
        setIsLoading(true);
        const [stats, histData] = await Promise.all([
          analyticsAPI.getAllTimeStats(),
          analyticsAPI.getHistoricalData(period)
        ]);

        if (!controller.signal.aborted) {
          setAllTimeStats(stats);
          setHistoricalData(histData || []);
        }
      } catch (error) {
        if (!controller.signal.aborted) {
          console.error('Failed to fetch analytics data:', error);
        }
      } finally {
        if (!controller.signal.aborted) {
          setIsLoading(false);
        }
      }
    }

    void fetchData();

    return () => {
      controller.abort();
    };
  }, [period]);

  return (
    <PageWrapper>
      <Header
        title="Dashboard Overview"
        description="Monitor your JWT authentication activity and system health at a glance. View analytics and key security metrics through interactive charts."
      />

      <div className="jwt-space-y-6">
        <MetricsSection isLoading={isLoading} allTimeStats={allTimeStats} />

        {!isLoading && historicalData.length > 0 && (
          <AnalyticsCharts
            historicalData={historicalData}
            defaultPeriod={period}
            onPeriodChange={setPeriod}
          />
        )}

        {!isWhitelabeled && (
          <div className="jwt-grid jwt-gap-6 jwt-grid-cols-1 md:jwt-grid-cols-2 lg:jwt-grid-cols-3">
            {quickLinks.map((link) => (
              <QuickLink key={link.title} {...link} />
            ))}
          </div>
        )}
      </div>
    </PageWrapper>
  );
}

