     5→namespace Tmeister\JWTAuthPro\Tests\Unit;
     6→
     7→use WP_UnitTestCase;
     8→
     9→abstract class TestCase extends WP_UnitTestCase
    10→{
    11→    /**
    12→     * Clean up database tables with foreign key constraints
    13→     */
    14→    protected function cleanupTables(): void
    15→    {
    16→        global $wpdb;
    17→
    18→        // Disable foreign key checks temporarily
    19→        $wpdb->query('SET FOREIGN_KEY_CHECKS = 0');
    20→
    21→        // Delete from analytics table first (child table)
    22→        $wpdb->query("TRUNCATE TABLE {$wpdb->prefix}jwt_auth_analytics");
    23→
    24→        // Then delete from tokens table (parent table)
    25→        $wpdb->query("TRUNCATE TABLE {$wpdb->prefix}jwt_auth_tokens");
    26→
    27→        // Re-enable foreign key checks
    28→        $wpdb->query('SET FOREIGN_KEY_CHECKS = 1');
    29→    }
    30→
    31→    protected function setUp(): void
    32→    {
    33→        parent::setUp();
    34→
    35→        // Add filter to prevent temporary tables
    36→        add_filter('query', [$this, 'preventTemporaryTables']);
    37→
    38→        // Define JWT secret if not defined
    39→        if (!defined('JWT_AUTH_SECRET_KEY')) {
    40→            define('JWT_AUTH_SECRET_KEY', 'test-secret-key');
    41→        }
    42→
    43→        // Define table names if not defined
    44→        if (!defined('JWT_AUTH_PRO_TOKENS_TABLE')) {
    45→            define('JWT_AUTH_PRO_TOKENS_TABLE', 'jwt_auth_tokens');
    46→        }
    47→        if (!defined('JWT_AUTH_PRO_ANALYTICS_TABLE')) {
    48→            define('JWT_AUTH_PRO_ANALYTICS_TABLE', 'jwt_auth_analytics');
    49→        }
    50→        if (!defined('JWT_AUTH_PRO_ANALYTICS_SUMMARY_TABLE')) {
    51→            define('JWT_AUTH_PRO_ANALYTICS_SUMMARY_TABLE', 'jwt_auth_analytics_summary');
    52→        }
    53→
    54→        // Clean up any existing data
    55→        $this->cleanupTables();
    56→    }
    57→
    58→    protected function tearDown(): void
    59→    {
    60→        // Clean up any test data
    61→        $this->cleanupTables();
    62→
    63→        // Remove the filter
    64→        remove_filter('query', [$this, 'preventTemporaryTables']);
    65→        parent::tearDown();
    66→    }
    67→
    68→    public function preventTemporaryTables(string $query): string
    69→    {
    70→        return str_replace('CREATE TEMPORARY TABLE', 'CREATE TABLE', $query);
    71→    }
    72→}
    73→

