    25→            'public_key' => 'pk_1c644825d6f2adf01066d1bf56c58',
    26→            'is_premium' => true,
    27→            'is_premium_only' => true,
    28→            'has_addons' => false,
    29→            'has_paid_plans' => true,
    30→            'is_org_compliant' => false,
    31→            'menu' => [
    32→                'slug' => 'jwt-auth-pro',
    33→                'contact' => false,
    34→                'support' => false,
    35→                'parent' => [
    36→                    'slug' => 'options-general.php',
