private function extractToken(WP_REST_Request $request): string|WP_Error
{
    $auth_header = $request->get_header('Authorization');

    // Check alternative header sources if standard Authorization header is not found
    if (!$auth_header) {
        // Check for HTTP_AUTHORIZATION in $_SERVER (common in Apache/Docker environments)
        if (isset($_SERVER['HTTP_AUTHORIZATION'])) {
            $auth_header = $_SERVER['HTTP_AUTHORIZATION'];
        }
        // Check for REDIRECT_HTTP_AUTHORIZATION (mod_rewrite scenarios)
        elseif (isset($_SERVER['REDIRECT_HTTP_AUTHORIZATION'])) {
            $auth_header = $_SERVER['REDIRECT_HTTP_AUTHORIZATION'];
        }
        // Check for Authorization via getallheaders() if available
        elseif (function_exists('getallheaders')) {
            $headers = getallheaders();
            if (isset($headers['Authorization'])) {
                $auth_header = $headers['Authorization'];
            } elseif (isset($headers['authorization'])) {
                $auth_header = $headers['authorization'];
            }
        }
    }

    if (!$auth_header) {
        return new WP_Error(
            'jwt_auth_no_auth_header',
            'Authorization header not found.',
            ['status' => 403]
        );
    }

    $token = str_replace('Bearer ', '', $auth_header);

    if (empty(trim($token))) {
        return new WP_Error(
            'jwt_auth_bad_auth_header',
            'Authorization header malformed.',
            ['status' => 403]
        );
    }

    return $token;
}

