private function extractToken(WP_REST_Request $request): string|WP_Error
{
    $auth_header = $request->get_header('Authorization');

    // In testing environments, try alternative sources for Authorization header
    if (!$auth_header && LicenseService::isTestingMode()) {
        // Try various $_SERVER keys where Authorization header might be
        if (isset($_SERVER['HTTP_AUTHORIZATION'])) {
            $auth_header = $_SERVER['HTTP_AUTHORIZATION'];
        } elseif (isset($_SERVER['REDIRECT_HTTP_AUTHORIZATION'])) {
            $auth_header = $_SERVER['REDIRECT_HTTP_AUTHORIZATION'];
        } elseif (function_exists('apache_request_headers')) {
            $headers = apache_request_headers();
            if (isset($headers['Authorization'])) {
                $auth_header = $headers['Authorization'];
            }
        }
    }

    if (!$auth_header) {
        return new WP_Error(
            'jwt_auth_no_auth_header',
            'Authorization header not found.',
            ['status' => 403]
        );
    }

