    26â†’    public function validateNonce(string $nonce): WP_Error|bool
    27â†’    {
    28â†’        if (!wp_verify_nonce($nonce, self::NONCE_ACTION)) {
    29â†’            return new WP_Error(
    30â†’                'jwt_auth_invalid_nonce',
    31â†’                'Invalid security token.',
    32â†’                ['status' => 403]
    33â†’            );
