     9â†’    private function getRequestIP(): string
    10â†’    {
    11â†’        $ip = '';
    12â†’
    13â†’        // Check if we're behind a proxy
    14â†’        if (isset($_SERVER['HTTP_X_FORWARDED_FOR'])) {
    15â†’            // HTTP_X_FORWARDED_FOR can contain a chain of comma-separated addresses
    16â†’            $ip = sanitize_text_field($_SERVER['HTTP_X_FORWARDED_FOR']);
    17â†’            $ip = trim(explode(',', $ip)[0]);
    18â†’        } elseif (isset($_SERVER['REMOTE_ADDR'])) {
    19â†’            $ip = sanitize_text_field($_SERVER['REMOTE_ADDR']);
    20â†’        }
    21â†’
    22â†’        // Additional headers for common proxy setups
    23â†’        $additionalHeaders = apply_filters('jwt_auth_ip_headers', [
    24â†’            'HTTP_CF_CONNECTING_IP', // Cloudflare
    25â†’            'HTTP_X_REAL_IP',       // Nginx proxy
    26â†’            'HTTP_CLIENT_IP',       // Client IP
    27â†’        ]);
    28â†’
    29â†’        // Only check additional headers if we haven't found an IP yet
    30â†’        if (empty($ip)) {
    31â†’            foreach ($additionalHeaders as $header) {
    32â†’                if (!empty($_SERVER[$header])) {
    33â†’                    $ip = sanitize_text_field($_SERVER[$header]);
    34â†’                    break;
    35â†’                }
    36â†’            }
    37â†’        }
    38â†’
    39â†’        // Validate the IP (both IPv4 and IPv6)
    40â†’        if (!empty($ip) && filter_var($ip, FILTER_VALIDATE_IP, FILTER_FLAG_IPV4 | FILTER_FLAG_IPV6)) {
    41â†’            return $ip;
    42â†’        }
    43â†’
    44â†’        return apply_filters('jwt_auth_default_ip', '');
    45â†’    }
    46â†’}
