   126â†’    private function validateFilters(array $filters): array|WP_Error
   127â†’    {
   128â†’        $allowedFilters   = ['user_id', 'status', 'created_at', 'expires_at', 'page', 'per_page', 'username'];
   129â†’        $sanitizedFilters = [];
   130â†’
   131â†’        foreach ($filters as $key => $value) {
   132â†’            if (!in_array($key, $allowedFilters, true)) {
   133â†’                continue;
   134â†’            }
   135â†’
   136â†’            switch ($key) {
   137â†’                case 'per_page':
   138â†’                    $value = absint($value);
   139â†’                    if ($value < 1 || $value > 100) {
   140â†’                        $value = 10; // Default to 10 if out of range
   141â†’                    }
   142â†’                    $sanitizedFilters[$key] = $value;
   143â†’                    break;
   144â†’                case 'page':
   145â†’                    $value = absint($value);
   146â†’                    if ($value < 1) {
   147â†’                        $value = 1;
   148â†’                    }
   149â†’                    $sanitizedFilters[$key] = $value;
   150â†’                    break;
   151â†’                case 'user_id':
   152â†’                    $sanitizedFilters[$key] = absint($value);
   153â†’                    break;
   154â†’                case 'username':
   155â†’                    if (empty($value)) {
   156â†’                        break;
   157â†’                    }
   158â†’                    $sanitizedFilters[$key] = sanitize_text_field($value);
   159â†’                    break;
   160â†’                case 'status':
   161â†’                    if (!in_array($value, ['active', 'expired', 'revoked'], true)) {
   162â†’                        return new WP_Error(
   163â†’                            'jwt_auth_invalid_status',
   164â†’                            'Invalid status filter.',
   165â†’                            ['status' => 400]
   166â†’                        );
   167â†’                    }
   168â†’                    $sanitizedFilters[$key] = sanitize_text_field($value);
   169â†’                    break;
   170â†’                case 'created_at':
   171â†’                case 'expires_at':
   172â†’                    if (!strtotime($value)) {
   173â†’                        return new WP_Error(
   174â†’                            'jwt_auth_invalid_date',
   175â†’                            'Invalid date format.',
   176â†’                            ['status' => 400]
   177â†’                        );
   178â†’                    }
   179â†’                    $sanitizedFilters[$key] = sanitize_text_field($value);
   180â†’                    break;
   181â†’            }
   182â†’        }
   183â†’
   184â†’        return $sanitizedFilters;
   185â†’    }
