    24â†’    public function aggregateDaily(): bool|WP_Error
    25â†’    {
    26â†’        $startDate = date('Y-m-d 00:00:00', strtotime('-1 day', strtotime(current_time('mysql', true))));
    27â†’        $endDate = date('Y-m-d 00:00:00', strtotime(current_time('mysql', true)));
    28â†’
    29â†’        $result = $this->repository->aggregateDaily($startDate, $endDate);
    30â†’
    31â†’        if (!is_wp_error($result)) {
