    44â†’    public function consolidateWeeklyData(): bool|WP_Error
    45â†’    {
    46â†’        try {
    47â†’            $endDate = new DateTime('yesterday', new DateTimeZone('UTC'));
    48â†’            $startDate = clone $endDate;
    49â†’            $startDate->modify('-6 days'); // This gives us 7 complete days, excluding today
    50â†’
    51â†’            // Process each day individually
    52â†’            $currentDate = clone $startDate;
    53â†’            $results = [];
    54â†’
    55â†’            while ($currentDate <= $endDate) {
    56â†’                $dayStart = $currentDate->format('Y-m-d 00:00:00');
    57â†’                $dayEnd = $currentDate->format('Y-m-d 23:59:59');
    58â†’
    59â†’                // Aggregate data for this specific day
    60â†’                $result = $this->repository->aggregateDaily($dayStart, $dayEnd);
    61â†’
    62â†’                if (is_wp_error($result)) {
    63â†’                    error_log(sprintf(
    64â†’                        'Failed to consolidate data for %s: %s',
    65â†’                        $currentDate->format('Y-m-d'),
    66â†’                        $result->get_error_message()
    67â†’                    ));
    68â†’                    $results[] = false;
    69â†’                } else {
    70â†’                    $results[] = true;
    71â†’                }
    72â†’
    73â†’                $currentDate->modify('+1 day');
    74â†’            }
    75â†’
    76â†’            // Clear cache if any day was successfully processed
    77â†’            if (in_array(true, $results, true)) {
    78â†’                $this->routesService->deleteAnalyticsTransients();
    79â†’
    80â†’                do_action('jwt_auth_analytics_consolidated', [
    81â†’                    'start_date' => $startDate->format('Y-m-d'),
    82â†’                    'end_date' => $endDate->format('Y-m-d'),
    83â†’                    'days_processed' => count(array_filter($results)),
    84â†’                ]);
    85â†’            }
    86â†’
    87â†’            // Return true if at least one day was processed successfully
    88â†’            return in_array(true, $results, true);
    89â†’        } catch (Exception $e) {
    90â†’            return new WP_Error(
    91â†’                'consolidation_failed',
    92â†’                'Failed to consolidate weekly data: ' . $e->getMessage()
    93â†’            );
    94â†’        }
    95â†’    }
