     8â†’{
     9â†’    private const TRANSIENT_PREFIX = 'jwt_auth_rate_limit_';
    10â†’
    11â†’    public function __construct() {}
    12â†’
    13â†’    /**
    14â†’     * Find rate limit record by IP address
    15â†’     */
    16â†’    public function findByIpAddress(string $ipAddress): ?object
    17â†’    {
    18â†’        $hashedIpAddress = hash('sha256', $ipAddress);
    19â†’        $data = get_transient(self::TRANSIENT_PREFIX . $hashedIpAddress);
    20â†’
    21â†’        if ($data === false) {
    22â†’            return null;
    23â†’        }
    24â†’
    25â†’        return (object) $data;
    26â†’    }
    27â†’
    28â†’    /**
    29â†’     * Create a new rate limit record
    30â†’     */
    31â†’    public function create(array $data): int|false
    32â†’    {
    33â†’        $hashedIpAddress = hash('sha256', $data['ip_address']);
    34â†’
    35â†’        $rateLimit = [
    36â†’            'ip_address' => $data['ip_address'],
    37â†’            'counter' => 1,
    38â†’            'window_start' => $data['window_start'],
    39â†’            'window_end' => $data['window_end'],
    40â†’        ];
    41â†’
    42â†’        // Calculate expiration in seconds from window_end
    43â†’        $expiration = strtotime($data['window_end']) - time();
    44â†’
    45â†’        $result = set_transient(
    46â†’            self::TRANSIENT_PREFIX . $hashedIpAddress,
    47â†’            $rateLimit,
    48â†’            $expiration
    49â†’        );
    50â†’
    51â†’        return $result ? 1 : false;
    52â†’    }
    53â†’
    54â†’    /**
    55â†’     * Increment the counter for a rate limit record
    56â†’     */
    57â†’    public function incrementCounter(string $ipAddress): bool
    58â†’    {
    59â†’        $hashedIpAddress = hash('sha256', $ipAddress);
    60â†’
    61â†’        $data = get_transient(self::TRANSIENT_PREFIX . $hashedIpAddress);
    62â†’
    63â†’        if ($data === false) {
    64â†’            return false;
    65â†’        }
    66â†’
    67â†’        $data['counter']++;
    68â†’
    69â†’        // Calculate remaining expiration time
    70â†’        $expiration = strtotime($data['window_end']) - time();
    71â†’
    72â†’        $result = set_transient(
    73â†’            self::TRANSIENT_PREFIX . $hashedIpAddress,
    74â†’            $data,
    75â†’            $expiration
    76â†’        );
    77â†’
    78â†’        return $result;
    79â†’    }
