    21â†’     * Check if the request is rate limited
    22â†’     */
    23â†’    public function isRateLimited(string $ipAddress): bool|WP_Error
    24â†’    {
    25â†’        // Allow short-circuiting the rate limit check, check the settings first and then honor the filter
    26â†’        $rateLimitEnabled = apply_filters('jwt_auth_rate_limit_enabled', $this->settingsService->getSetting('rate_limit_settings', 'enabled'));
