    52â†’            $error = new WP_Error(
    53â†’                'jwt_auth_rate_limited',
    54â†’                'Rate limit exceeded',
    55â†’                [
    56â†’                    'status' => 429,
    57â†’                    'retry_after' => $retryAfter,
    58â†’                ]
    59â†’            );
    60â†’
    61â†’            $this->addRateLimitHeaders($maxRequests, 0, strtotime($rateLimit->window_end), $retryAfter);
    62â†’            return $error;
    63â†’        }
    64â†’
    65â†’        // Increment the counter
    66â†’        $this->repository->incrementCounter($ipAddress);
    67â†’
    68â†’        // Add rate limit headers for successful requests
    69â†’        $remaining = max(0, $maxRequests - ($rateLimit->counter + 1));
    70â†’        $this->addRateLimitHeaders($maxRequests, $remaining, strtotime($rateLimit->window_end));
    71â†’
    72â†’        return true;
    73â†’    }
