   222â†’    public function aggregateDaily(string $startDate, string $endDate): bool|WP_Error
   223â†’    {
   224â†’        // Validate date formats to prevent SQL injection
   225â†’        if (!preg_match('/^\d{4}-\d{2}-\d{2}/', $startDate) || !preg_match('/^\d{4}-\d{2}-\d{2}/', $endDate)) {
   226â†’            return new WP_Error(
   227â†’                'invalid_date_format',
   228â†’                'Invalid date format. Expected YYYY-MM-DD',
   229â†’                ['status' => 400]
   230â†’            );
   231â†’        }
   232â†’
   233â†’        try {
   234â†’            // Start transaction
   235â†’            $this->wpdb->query('START TRANSACTION');
   236â†’
   237â†’            // Generate all dates in the range
   238â†’            $dates = [];
   239â†’            $current = new DateTime($startDate);
   240â†’            $end = new DateTime($endDate);
   241â†’
   242â†’            while ($current <= $end) {
   243â†’                $dates[] = $current->format('Y-m-d');
   244â†’                $current->modify('+1 day');
   245â†’            }
   246â†’
   247â†’            // Get token stats
   248â†’            $tokenStats = $this->getTokenStats($startDate, $endDate);
   249â†’            if (is_wp_error($tokenStats)) {
   250â†’                throw new Exception($tokenStats->get_error_message());
   251â†’            }
   252â†’
   253â†’            // Get analytics stats
   254â†’            $analyticsStats = $this->getAnalyticsStats($startDate, $endDate);
   255â†’            if (is_wp_error($analyticsStats)) {
   256â†’                throw new Exception($analyticsStats->get_error_message());
   257â†’            }
   258â†’
   259â†’            // Create lookup arrays for easy access
   260â†’            $tokenStatsByDate = array_column($tokenStats, null, 'summary_date');
   261â†’            $analyticsStatsByDate = array_column($analyticsStats, null, 'summary_date');
   262â†’
   263â†’            // Process each date in the range
   264â†’            foreach ($dates as $date) {
   265â†’                // Prepare token stats data
   266â†’                $tokenData = [
   267â†’                    'summary_date' => $date,
   268â†’                    'blog_id' => get_current_blog_id(),
   269â†’                    'metrics' => isset($tokenStatsByDate[$date])
   270â†’                        ? json_encode($tokenStatsByDate[$date]['metrics'])
   271â†’                        : json_encode([
   272â†’                            'total_tokens' => 0,
   273â†’                            'user_count' => 0,
   274â†’                            'active_tokens' => 0,
   275â†’                            'revoked_tokens' => 0,
   276â†’                            'expired_tokens' => 0,
   277â†’                        ]),
   278â†’                    'metadata' => isset($tokenStatsByDate[$date])
   279â†’                        ? json_encode($tokenStatsByDate[$date]['metadata'])
   280â†’                        : json_encode([
   281â†’                            'token_families' => 0,
   282â†’                            'growth_rate' => [
   283â†’                                'daily' => 0,
   284â†’                                'total' => 0,
   285â†’                                'percentage' => 0,
   286â†’                            ],
   287â†’                        ]),
   288â†’                ];
   289â†’
   290â†’                // Prepare analytics stats data
   291â†’                $analyticsData = [
   292â†’                    'summary_date' => $date,
   293â†’                    'blog_id' => get_current_blog_id(),
   294â†’                    'metrics' => isset($analyticsStatsByDate[$date])
   295â†’                        ? json_encode($analyticsStatsByDate[$date]['metrics'])
   296â†’                        : json_encode([
   297â†’                            'total_requests' => 0,
   298â†’                            'successful_requests' => 0,
   299â†’                            'failed_requests' => 0,
   300â†’                            'unique_users' => 0,
   301â†’                            'avg_response_time' => 0,
   302â†’                        ]),
   303â†’                    'metadata' => isset($analyticsStatsByDate[$date])
   304â†’                        ? json_encode($analyticsStatsByDate[$date]['metadata'])
   305â†’                        : json_encode([
   306â†’                            'events_by_type' => [
   307â†’                                'token_generation' => ['total' => 0, 'success' => 0, 'failure' => 0],
   308â†’                                'token_validation' => ['total' => 0, 'success' => 0, 'failure' => 0],
   309â†’                                'token_refresh' => ['total' => 0, 'success' => 0, 'failure' => 0],
   310â†’                            ],
   311â†’                        ]),
   312â†’                ];
   313â†’
   314â†’                // Insert or update records
   315â†’                $this->upsertSummaryRecord($tokenData, 1);
   316â†’                $this->upsertSummaryRecord($analyticsData, 2);
   317â†’            }
   318â†’
   319â†’            // Commit transaction
   320â†’            $this->wpdb->query('COMMIT');
   321â†’            return true;
   322â†’        } catch (Exception $e) {
   323â†’            // Rollback on error
   324â†’            $this->wpdb->query('ROLLBACK');
   325â†’
   326â†’            error_log(sprintf(
   327â†’                'Analytics aggregation failed: %s. Start Date: %s, End Date: %s',
   328â†’                $e->getMessage(),
   329â†’                $startDate,
   330â†’                $endDate
   331â†’            ));
   332â†’
   333â†’            return new WP_Error(
   334â†’                'jwt_auth_analytics_error',
   335â†’                $e->getMessage(),
   336â†’                ['status' => 500]
   337â†’            );
   338â†’        }
   339â†’    }
