   341â†’    private function getTokenStats(string $startDate, string $endDate): array|WP_Error
   342â†’    {
   343â†’        $query = $this->wpdb->prepare(
   344â†’            'SELECT
   345â†’                DATE(t.issued_at) as summary_date,
   346â†’                t.blog_id,
   347â†’                COUNT(1) as total_tokens,
   348â†’                COUNT(DISTINCT t.user_id) as user_count,
   349â†’                SUM(CASE WHEN t.revoked_at IS NULL AND t.expires_at > DATE_ADD(DATE(t.issued_at), INTERVAL 1 DAY) THEN 1 ELSE 0 END) as active_tokens,
   350â†’                SUM(CASE WHEN t.revoked_at IS NOT NULL THEN 1 ELSE 0 END) as revoked_tokens,
   351â†’                SUM(CASE WHEN t.expires_at <= DATE_ADD(DATE(t.issued_at), INTERVAL 1 DAY) AND t.revoked_at IS NULL THEN 1 ELSE 0 END) as expired_tokens,
   352â†’                COUNT(DISTINCT t.token_family) as token_families,
   353â†’                (SELECT COUNT(1) FROM %i) as total_tokens_all_time
   354â†’            FROM %i t
   355â†’            WHERE t.issued_at >= %s
   356â†’            AND t.issued_at < %s
   357â†’            GROUP BY DATE(t.issued_at), t.blog_id',
   358â†’            $this->tokens_table,
   359â†’            $this->tokens_table,
   360â†’            $startDate,
   361â†’            $endDate
   362â†’        );
   363â†’
   364â†’        $results = $this->wpdb->get_results($query, ARRAY_A);
   365â†’        if ($results === null) {
   366â†’            return new WP_Error('query_failed', $this->wpdb->last_error);
   367â†’        }
   368â†’
   369â†’        return array_map(function ($row) {
   370â†’            return [
   371â†’                'summary_date' => $row['summary_date'],
   372â†’                'blog_id' => $row['blog_id'],
   373â†’                'metrics' => [
   374â†’                    'total_tokens' => (int) $row['total_tokens'],
   375â†’                    'user_count' => (int) $row['user_count'],
   376â†’                    'active_tokens' => (int) $row['active_tokens'],
   377â†’                    'revoked_tokens' => (int) $row['revoked_tokens'],
   378â†’                    'expired_tokens' => (int) $row['expired_tokens'],
   379â†’                ],
   380â†’                'metadata' => [
   381â†’                    'token_families' => (int) $row['token_families'],
   382â†’                    'growth_rate' => [
   383â†’                        'daily' => (int) $row['total_tokens'],
   384â†’                        'total' => (int) $row['total_tokens_all_time'],
   385â†’                        'percentage' => $row['total_tokens_all_time'] > 0
   386â†’                            ? round(($row['total_tokens'] / $row['total_tokens_all_time']) * 100, 2)
   387â†’                            : 0,
   388â†’                    ],
   389â†’                ],
   390â†’            ];
   391â†’        }, $results);
   392â†’    }
