   394â†’    private function getAnalyticsStats(string $startDate, string $endDate): array|WP_Error
   395â†’    {
   396â†’        $query = $this->wpdb->prepare(
   397â†’            'WITH event_stats AS (
   398â†’                SELECT
   399â†’                    DATE(event_timestamp) as summary_date,
   400â†’                    blog_id,
   401â†’                    event_type,
   402â†’                    COUNT(*) as total,
   403â†’                    SUM(CASE WHEN event_status = 1 THEN 1 ELSE 0 END) as success,
   404â†’                    SUM(CASE WHEN event_status = 2 THEN 1 ELSE 0 END) as failure,
   405â†’                    COUNT(DISTINCT user_id) as unique_users,
   406â†’                    CAST(AVG(response_time) AS UNSIGNED) as avg_response_time
   407â†’                FROM %i
   408â†’                WHERE event_timestamp >= %s
   409â†’                AND event_timestamp < %s
   410â†’                GROUP BY DATE(event_timestamp), blog_id, event_type
   411â†’            )
   412â†’            SELECT
   413â†’                summary_date,
   414â†’                blog_id,
   415â†’                SUM(total) as total_requests,
   416â†’                SUM(success) as successful_requests,
   417â†’                SUM(failure) as failed_requests,
   418â†’                MAX(unique_users) as unique_users,
   419â†’                AVG(avg_response_time) as avg_response_time,
   420â†’                GROUP_CONCAT(
   421â†’                    CONCAT(
   422â†’                        event_type, ":",
   423â†’                        total, ":",
   424â†’                        success, ":",
   425â†’                        failure
   426â†’                    )
   427â†’                ) as event_details
   428â†’            FROM event_stats
   429â†’            GROUP BY summary_date, blog_id',
   430â†’            $this->analytics_table,
   431â†’            $startDate,
   432â†’            $endDate
   433â†’        );
   434â†’
   435â†’        $results = $this->wpdb->get_results($query, ARRAY_A);
   436â†’        if ($results === null) {
   437â†’            return new WP_Error('query_failed', $this->wpdb->last_error);
   438â†’        }
   439â†’
   440â†’        return array_map(function ($row) {
   441â†’            $eventsByType = [];
   442â†’            $eventDetails = explode(',', $row['event_details']);
   443â†’            foreach ($eventDetails as $detail) {
   444â†’                [$type, $total, $success, $failure] = explode(':', $detail);
   445â†’                $eventsByType[$type] = [
   446â†’                    'total' => (int) $total,
   447â†’                    'success' => (int) $success,
   448â†’                    'failure' => (int) $failure,
   449â†’                ];
   450â†’            }
   451â†’
   452â†’            return [
   453â†’                'summary_date' => $row['summary_date'],
   454â†’                'blog_id' => $row['blog_id'],
   455â†’                'metrics' => [
   456â†’                    'total_requests' => (int) $row['total_requests'],
   457â†’                    'successful_requests' => (int) $row['successful_requests'],
   458â†’                    'failed_requests' => (int) $row['failed_requests'],
   459â†’                    'unique_users' => (int) $row['unique_users'],
   460â†’                    'avg_response_time' => (int) $row['avg_response_time'],
   461â†’                ],
   462â†’                'metadata' => [
   463â†’                    'events_by_type' => $eventsByType,
   464â†’                ],
   465â†’            ];
   466â†’        }, $results);
   467â†’    }
