   469â†’    public function getTokenSummary(string $period): array|WP_Error
   470â†’    {
   471â†’        try {
   472â†’            $startDate = $this->getStartDateForPeriod($period);
   473â†’            $currentTime = gmdate('Y-m-d H:i:s');
   474â†’
   475â†’            $result = $this->wpdb->get_row(
   476â†’                $this->wpdb->prepare(
   477â†’                    'SELECT
   478â†’                        COUNT(1) as total_tokens,
   479â†’                        COUNT(DISTINCT user_id) as user_count,
   480â†’                        SUM(
   481â†’                            CASE
   482â†’                                WHEN t.revoked_at IS NULL
   483â†’                                AND t.expires_at > %s
   484â†’                                THEN 1
   485â†’                                ELSE 0
   486â†’                            END
   487â†’                        ) as active_tokens,
   488â†’                        SUM(
   489â†’                            CASE
   490â†’                                WHEN t.revoked_at IS NOT NULL
   491â†’                                THEN 1
   492â†’                                ELSE 0
   493â†’                            END
   494â†’                        ) as revoked_tokens,
   495â†’                        SUM(
   496â†’                            CASE
   497â†’                                WHEN t.expires_at <= %s
