   499â†’                                THEN 1
   500â†’                                ELSE 0
   501â†’                            END
   502â†’                        ) as expired_tokens
   503â†’                    FROM (
   504â†’                        SELECT user_id, expires_at, revoked_at
   505â†’                        FROM %i
   506â†’                        FORCE INDEX (issued_at_idx)
   507â†’                        WHERE issued_at >= %s
   508â†’                    ) t',
   509â†’                    $currentTime,
   510â†’                    $currentTime,
   511â†’                    $this->tokens_table,
   512â†’                    $startDate
   513â†’                ),
   514â†’                ARRAY_A
   515â†’            );
   516â†’
   517â†’            return $result ?: [
   518â†’                'total_tokens' => 0,
   519â†’                'user_count' => 0,
   520â†’                'active_tokens' => 0,
   521â†’                'revoked_tokens' => 0,
   522â†’                'expired_tokens' => 0,
   523â†’            ];
   524â†’        } catch (Exception $e) {
   525â†’            return new WP_Error('analytics_error', $e->getMessage());
   526â†’        }
   527â†’    }
