   654â†’                        'metadata' => json_decode($result['metadata'], true),
   655â†’                    ];
   656â†’                }
   657â†’            }
   658â†’
   659â†’            // If still no data, try to get data from the analytics table directly
   660â†’            if ($summary['token_stats'] === null || $summary['analytics'] === null) {
   661â†’                $tokenStats = $this->getTokenSummary('24h');
   662â†’                if (!is_wp_error($tokenStats)) {
   663â†’                    $summary['token_stats'] = [
   664â†’                        'metrics' => $tokenStats,
   665â†’                        'metadata' => [
   666â†’                            'growth_rate' => [
