   683â†’    public function getHistoricalData(string $period = '7d'): array|WP_Error
   684â†’    {
   685â†’        try {
   686â†’            $startDate = $this->getStartDateForPeriod($period);
   687â†’            $endDate = gmdate('Y-m-d');
   688â†’
   689â†’            // Generate all dates in the range
   690â†’            $dates = [];
   691â†’            $currentDate = new DateTime($startDate);
   692â†’            $endDateTime = new DateTime($endDate);
   693â†’
   694â†’            while ($currentDate <= $endDateTime) {
   695â†’                $dates[] = $currentDate->format('Y-m-d');
   696â†’                $currentDate->modify('+1 day');
   697â†’            }
   698â†’
   699â†’            $results = $this->wpdb->get_results(
   700â†’                $this->wpdb->prepare(
   701â†’                    'SELECT
   702â†’                        s.summary_date,
   703â†’                        s.summary_type,
   704â†’                        s.metrics,
   705â†’                        s.metadata
   706â†’                    FROM %i s
   707â†’                    WHERE s.summary_date >= %s
   708â†’                    AND s.summary_date <= %s
   709â†’                    AND s.blog_id = %d
   710â†’                    ORDER BY s.summary_date ASC, s.summary_type ASC',
   711â†’                    $this->summary_table,
   712â†’                    $startDate,
   713â†’                    $endDate,
   714â†’                    get_current_blog_id()
   715â†’                ),
   716â†’                ARRAY_A
   717â†’            );
   718â†’
   719â†’            $historicalData = [];
   720â†’
   721â†’            // Initialize all dates with zero values
   722â†’            foreach ($dates as $date) {
   723â†’                $historicalData[$date] = [
   724â†’                    'summary_date' => $date,
   725â†’                    'token_stats' => [
   726â†’                        'metrics' => [
   727â†’                            'total_tokens' => 0,
   728â†’                            'user_count' => 0,
   729â†’                            'active_tokens' => 0,
   730â†’                            'revoked_tokens' => 0,
   731â†’                            'expired_tokens' => 0,
   732â†’                        ],
   733â†’                        'metadata' => [
   734â†’                            'token_families' => 0,
   735â†’                            'growth_rate' => [
   736â†’                                'daily' => 0,
   737â†’                                'total' => 0,
   738â†’                                'percentage' => 0,
   739â†’                            ],
   740â†’                        ],
   741â†’                    ],
   742â†’                    'analytics' => [
   743â†’                        'metrics' => [
   744â†’                            'total_requests' => 0,
   745â†’                            'successful_requests' => 0,
   746â†’                            'failed_requests' => 0,
   747â†’                            'unique_users' => 0,
   748â†’                            'avg_response_time' => 0,
   749â†’                        ],
   750â†’                        'metadata' => [
   751â†’                            'events_by_type' => [
   752â†’                                'token_generation' => ['total' => 0, 'success' => 0, 'failure' => 0],
   753â†’                                'token_validation' => ['total' => 0, 'success' => 0, 'failure' => 0],
   754â†’                                'token_refresh' => ['total' => 0, 'success' => 0, 'failure' => 0],
   755â†’                            ],
   756â†’                        ],
   757â†’                    ],
   758â†’                ];
   759â†’            }
   760â†’
   761â†’            // Fill in actual data where available
   762â†’            if (!empty($results)) {
   763â†’                foreach ($results as $result) {
   764â†’                    $date = $result['summary_date'];
   765â†’                    $type = $result['summary_type'] === '1' ? 'token_stats' : 'analytics';
   766â†’
   767â†’                    if (isset($historicalData[$date])) {
