export interface TokenStats {
  metrics: {
    total_tokens: number;
    total_tokens_today: number;
    total_tokens_yesterday: number;
    active_tokens: number;
    revoked_tokens: number;
    expired_tokens: number;
    total_requests: number;
    total_requests_today: number;
    total_requests_yesterday: number;
    successful_requests: number;
    failed_requests: number;
  };
  metadata?: {
    growth_rate?: { percentage: number };
    token_families?: number;
  };
}

export interface Analytics {
  metrics: {
    total_requests: number;
    successful_requests: number;
    failed_requests: number;
    unique_users: number;
    avg_response_time: number;
  };
  metadata: {
    events_by_type: {
      token_generation: {
        total: number;
        success: number;
        failure: number;
      };
      token_validation: {
        total: number;
        success: number;
        failure: number;
      };
      token_refresh: {
        total: number;
        success: number;
        failure: number;
      };
    };
    top_failure_reasons: Array<{
      reason: string;
      count: number;
    }>;
    unique_ips: number;
    unique_user_agents: number;
    countries: Record<string, number>;
  };
}

export interface DashboardSummary {
  token_stats: TokenStats;
  analytics: Analytics;
}

export interface HistoricalData extends DashboardSummary {
  summary_date: string;
}

