'use client';

import { useEffect, useState } from 'react';
import { Header } from '@/components/ui/header';
import { PageWrapper } from '@/components/ui/page-wrapper';
import { systemAPI } from '@/services/api';
import type { SystemInfo } from '@/types/api';
import { SystemOverviewCard } from '@/components/system/system-overview-card';
import { Button } from '@/components/ui/button';
import { useToast } from '@/components/hooks/use-toast';
import { ClipboardCopy } from 'lucide-react';

export default function SystemPage() {
  const [isLoading, setIsLoading] = useState(true);
  const [systemInfo, setSystemInfo] = useState<SystemInfo | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    let isMounted = true;

    const fetchData = async () => {
      try {
        setIsLoading(true);
        const systemData = await systemAPI.getInfo();

        if (isMounted) {
          setSystemInfo(systemData);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        if (isMounted) {
          setIsLoading(false);
        }
      }
    };

    void fetchData();

    return () => {
      isMounted = false;
    };
  }, []);

  const formatSystemInfo = (info: SystemInfo): string => {
    return `
### JWT Auth Pro System Information ###

WordPress Environment:
- WordPress Version: ${info.wordpress_version.version}
- Site URL: ${info.wordpress.site_url}
- Home URL: ${info.wordpress.home_url}
- Multisite: ${info.wordpress.is_multisite ? 'Yes' : 'No'}
- Memory Limit: ${info.wordpress.memory_limit}
- Language: ${info.wordpress.language}
- Timezone: ${info.wordpress.timezone.string} (${info.wordpress.timezone.gmt_offset})

Server Environment:
- PHP Version: ${info.php_version.version}
- MySQL Version: ${info.mysql_version.version}
- Web Server: ${info.server.software}
- Operating System: ${info.server.os}
- Architecture: ${info.server.architecture}
- SSL Installed: ${info.server.ssl_installed ? 'Yes' : 'No'}

PHP Configuration:
- Memory Limit: ${info.php.memory_limit}
- Max Execution Time: ${info.php.max_execution_time}
- Post Max Size: ${info.php.post_max_size}
- Upload Max Filesize: ${info.php.upload_max_filesize}
- Max Input Vars: ${info.php.max_input_vars}
- Display Errors: ${info.php.display_errors}
- OPCache Enabled: ${info.php.opcache_enabled ? 'Yes' : 'No'}

WordPress Constants:
- WP_DEBUG: ${info.constants.WP_DEBUG ? 'Yes' : 'No'}
- WP_DEBUG_LOG: ${info.constants.WP_DEBUG_LOG ? 'Yes' : 'No'}
- WP_DEBUG_DISPLAY: ${info.constants.WP_DEBUG_DISPLAY ? 'Yes' : 'No'}
- WP_CACHE: ${info.constants.WP_CACHE ? 'Yes' : 'No'}

Active Theme:
- Name: ${info.theme.name}
- Version: ${info.theme.version}
- Author: ${info.theme.author}

Active Plugins: ${info.plugins.active}/${info.plugins.total}
${info.plugins.items
  .filter((plugin) => plugin.status === 'active')
  .map((plugin) => `- ${plugin.name} ${plugin.version} by ${plugin.author}`)
  .join('\n')}

Inactive Plugins: ${info.plugins.total - info.plugins.active}/${info.plugins.total}
${info.plugins.items
  .filter((plugin) => plugin.status === 'inactive')
  .map((plugin) => `- ${plugin.name} ${plugin.version} by ${plugin.author}`)
  .join('\n')}

### End System Information ###
    `.trim();
  };

  const handleCopySystemInfo = async () => {
    if (!systemInfo) return;

    try {
      const formattedInfo = formatSystemInfo(systemInfo);
      await navigator.clipboard.writeText(formattedInfo);
      toast({
        title: 'Success',
        description: 'System information copied to clipboard'
      });
    } catch (error) {
      console.error('Copy failed:', error);
      toast({
        title: 'Error',
        description: 'Failed to copy system information',
        variant: 'destructive'
      });
    }
  };

  return (
    <PageWrapper>
      <div className="jwt-flex jwt-items-center jwt-justify-between">
        <Header
          title="System Status & Information"
          description="Manage and monitor your JWT authAccess technical details about your JWT authentication system. Review logs, performance metrics, and environment information.."
        />
        <Button
          onClick={handleCopySystemInfo}
          disabled={isLoading || !systemInfo}
          className="jwt-flex jwt-items-center jwt-gap-2 jwt-pl-10"
        >
          <ClipboardCopy className="jwt-h-4 jwt-w-4" />
          Copy System Info
        </Button>
      </div>
      <div className="jwt-space-y-6">
        <SystemOverviewCard isLoading={isLoading} data={systemInfo} />
      </div>
    </PageWrapper>
  );
}

