import { AppSidebar } from '@/components/app-sidebar';
import { Toaster } from '@/components/ui/toaster';

interface LayoutProps {
  children: React.ReactNode;
}

export default function Layout({ children }: LayoutProps) {
  return (
    <div className="jwt-flex jwt-h-[calc(100vh-32px)] jwt-w-full jwt-overflow-hidden">
      <AppSidebar />
      <main className="jwt-flex-1 jwt-overflow-auto jwt-bg-gray-50 jwt-px-4 md:jwt-px-10">
        <div className="jwt-border jwt-shadow-sm jwt-rounded jwt-my-5 jwt-bg-white jwt-max-w-7xl jwt-mx-auto jwt-p-6">
          {children}
        </div>
      </main>
      <Toaster />
    </div>
  );
}

'use client';

import { Card, CardContent, CardHeader } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Info, Loader2 } from 'lucide-react';
import { cn } from '@/lib/utils';

interface PluginInformationCardProps {
  className?: string;
  currentVersion: string;
  latestVersion: string;
  isUpdateAvailable: boolean;
  isChecking?: boolean;
  onCheckUpdate?: () => void;
}

export function PluginInformationCard({
  className,
  currentVersion,
  latestVersion,
  isUpdateAvailable,
  isChecking = false,
  onCheckUpdate
}: PluginInformationCardProps) {
  return (
    <Card className={cn('jwt-w-full jwt-h-full', className)}>
      <div className="jwt-h-full jwt-flex jwt-flex-col">
        <CardHeader className="jwt-space-y-3">
          <div className="jwt-flex jwt-items-center jwt-space-x-2">
            <Info className="jwt-h-5 jwt-w-5 jwt-text-muted-foreground" />
            <h2 className="jwt-text-lg jwt-font-semibold jwt-text-primary">Plugin Information</h2>
          </div>
        </CardHeader>
        <CardContent className="jwt-flex-1 jwt-flex jwt-flex-col jwt-justify-between">
          <dl className="jwt-space-y-4">
            <div className="jwt-flex jwt-items-center jwt-justify-between">
              <dt className="jwt-font-medium">Current Version</dt>
              <dd className="jwt-font-semibold">{currentVersion}</dd>
            </div>
            <div className="jwt-flex jwt-items-center jwt-justify-between">
              <dt className="jwt-font-medium">Latest Version</dt>
              <dd className="jwt-font-semibold">{latestVersion}</dd>
            </div>
            <div className="jwt-flex jwt-items-center jwt-justify-between">
              <dt className="jwt-font-medium">Update Available</dt>
              <dd className="jwt-font-semibold">{isUpdateAvailable ? 'Yes' : 'No'}</dd>
            </div>
          </dl>

          <Button className="jwt-w-full jwt-mt-6" disabled={isChecking} onClick={onCheckUpdate}>
            {isChecking && <Loader2 className="jwt-mr-2 jwt-h-4 jwt-w-4 jwt-animate-spin" />}
            Check For Updates
          </Button>
        </CardContent>
      </div>
    </Card>
  );
}

