     7â†’use Closure;
     8â†’use InvalidArgumentException;
     9â†’use ReflectionClass;
    10â†’use ReflectionException;
    11â†’use ReflectionNamedType;
    12â†’use ReflectionParameter;
    13â†’
    14â†’class Container
    15â†’{
    16â†’    /**
    17â†’     * The container's bindings.
    18â†’     *
    19â†’     * @var array<string, Closure>
    20â†’     */
    21â†’    private array $bindings = [];
    22â†’
    23â†’    /**
    24â†’     * The container's shared instances.
    25â†’     *
    26â†’     * @var array<string, mixed>
    27â†’     */
    28â†’    private array $instances = [];
    29â†’
    30â†’    /**
    31â†’     * Register a binding with the container.
    32â†’     */
    33â†’    public function bind(string $abstract, Closure $concrete, bool $shared = false): void
    34â†’    {
    35â†’        $this->bindings[$abstract] = $concrete;
    36â†’
    37â†’        if ($shared) {
    38â†’            $this->instances[$abstract] = null;
    39â†’        }
    40â†’    }
    41â†’
    42â†’    /**
    43â†’     * Register a shared binding in the container.
    44â†’     */
    45â†’    public function singleton(string $abstract, Closure $concrete): void
    46â†’    {
    47â†’        $this->bind($abstract, $concrete, true);
    48â†’    }
    49â†’
    50â†’    /**
    51â†’     * Resolve the given type from the container.
    52â†’     *
    53â†’     * @template T
    54â†’     * @param class-string<T> $abstract
    55â†’     * @return T
    56â†’     *
    57â†’     * @throws ReflectionException
    58â†’     */
    59â†’    public function make(string $abstract): mixed
    60â†’    {
    61â†’        return match (true) {
    62â†’            isset($this->instances[$abstract]) => $this->instances[$abstract],
    63â†’            isset($this->bindings[$abstract]) => $this->resolveBinding($abstract),
    64â†’            default => $this->build($abstract),
    65â†’        };
    66â†’    }
    67â†’
    68â†’    /**
    69â†’     * Resolve a binding from the container.
    70â†’     */
    71â†’    private function resolveBinding(string $abstract): mixed
    72â†’    {
    73â†’        $concrete = $this->bindings[$abstract];
    74â†’        $object = $concrete($this);
    75â†’
    76â†’        if (isset($this->instances[$abstract])) {
    77â†’            $this->instances[$abstract] = $object;
    78â†’        }
    79â†’
    80â†’        return $object;
    81â†’    }
    82â†’
    83â†’    /**
    84â†’     * Instantiate a concrete instance of the given type.
    85â†’     *
    86â†’     * @throws ReflectionException
    87â†’     */
    88â†’    private function build(string $concrete): object
    89â†’    {
    90â†’        $reflector = new ReflectionClass($concrete);
    91â†’
    92â†’        if (!$reflector->isInstantiable()) {
    93â†’            throw new InvalidArgumentException("Target [$concrete] is not instantiable.");
    94â†’        }
    95â†’
    96â†’        $constructor = $reflector->getConstructor();
    97â†’
    98â†’        if ($constructor === null) {
    99â†’            return new $concrete();
   100â†’        }
   101â†’
   102â†’        $dependencies = array_map(
   103â†’            fn($parameter) => $this->resolveDependency($parameter),
   104â†’            $constructor->getParameters()
   105â†’        );
   106â†’
   107â†’        return new $concrete(...$dependencies);
   108â†’    }
   109â†’
   110â†’    /**
   111â†’     * Resolve a constructor parameter dependency.
   112â†’     */
   113â†’    private function resolveDependency(ReflectionParameter $parameter): mixed
   114â†’    {
   115â†’        $type = $parameter->getType();
   116â†’
   117â†’        if ($type instanceof ReflectionNamedType && !$type->isBuiltin()) {
   118â†’            return $this->make($type->getName());
   119â†’        }
   120â†’
   121â†’        if ($parameter->isDefaultValueAvailable()) {
   122â†’            return $parameter->getDefaultValue();
   123â†’        }
   124â†’
   125â†’        throw new InvalidArgumentException(
   126â†’            "Unable to resolve dependency [{$parameter->getName()}]"
   127â†’        );
   128â†’    }
   129â†’
   130â†’    /**
   131â†’     * Determine if a given type has been bound.
   132â†’     */
   133â†’    public function bound(string $abstract): bool
   134â†’    {
   135â†’        return isset($this->bindings[$abstract]) || isset($this->instances[$abstract]);
   136â†’    }
   137â†’
   138â†’    /**
   139â†’     * Get a shared instance of the given type.
   140â†’     *
   141â†’     * @throws ReflectionException
   142â†’     */
   143â†’    public function getInstance(string $abstract): mixed
   144â†’    {
   145â†’        if (!isset($this->instances[$abstract])) {
   146â†’            $this->instances[$abstract] = $this->make($abstract);
   147â†’        }
   148â†’
   149â†’        return $this->instances[$abstract];
   150â†’    }
   151â†’}
