     5â†’namespace Tmeister\JWTAuthPro\Services;
     6â†’
     7â†’use Tmeister\JWTAuthPro\Database\RateLimitRepository;
     8â†’use Tmeister\JWTAuthPro\Traits\HasRequestIP;
     9â†’use WP_Error;
    10â†’
    11â†’class RateLimitService
    12â†’{
    13â†’    use HasRequestIP;
    14â†’
    15â†’    public function __construct(
    16â†’        private readonly RateLimitRepository $repository,
    17â†’        private readonly SettingsService $settingsService,
    18â†’    ) {}
    19â†’
    20â†’    /**
    21â†’     * Check if the request is rate limited
    22â†’     */
    23â†’    public function isRateLimited(string $ipAddress): bool|WP_Error
    24â†’    {
    25â†’        // Allow short-circuiting the rate limit check, check the settings first and then honor the filter
    26â†’        $rateLimitEnabled = apply_filters('jwt_auth_rate_limit_enabled', $this->settingsService->getSetting('rate_limit_settings', 'enabled'));
    27â†’        if (!$rateLimitEnabled) {
    28â†’            return true;
    29â†’        }
    30â†’
    31â†’        // Get the rate limit config
    32â†’        $maxRequests = $this->getRateLimitRequests();
    33â†’        $windowMinutes = $this->getRateLimitWindowMinutes();
    34â†’
    35â†’        // Check if we have an existing rate limit record
    36â†’        $rateLimit = $this->repository->findByIpAddress($ipAddress);
    37â†’
    38â†’        if ($rateLimit === null) {
    39â†’            // Create new rate limit record with UTC timestamps
    40â†’            $now = gmdate('Y-m-d H:i:s');
    41â†’            $windowEnd = gmdate('Y-m-d H:i:s', strtotime($now) + ($windowMinutes * 60));
    42â†’            $this->repository->create(['ip_address' => $ipAddress, 'window_start' => $now, 'window_end' => $windowEnd]);
    43â†’            $this->addRateLimitHeaders($maxRequests, $maxRequests - 1, strtotime($windowEnd));
    44â†’
    45â†’            return true;
    46â†’        }
    47â†’
    48â†’        if ($rateLimit->counter >= $maxRequests) {
    49â†’            $retryAfter = strtotime($rateLimit->window_end) - time();
    50â†’
    51â†’            // Create error response with headers
    52â†’            $error = new WP_Error(
    53â†’                'jwt_auth_rate_limited',
    54â†’                'Rate limit exceeded',
    55â†’                [
    56â†’                    'status' => 429,
    57â†’                    'retry_after' => $retryAfter,
    58â†’                ]
    59â†’            );
    60â†’
    61â†’            $this->addRateLimitHeaders($maxRequests, 0, strtotime($rateLimit->window_end), $retryAfter);
    62â†’            return $error;
    63â†’        }
    64â†’
    65â†’        // Increment the counter
    66â†’        $this->repository->incrementCounter($ipAddress);
    67â†’
    68â†’        // Add rate limit headers for successful requests
    69â†’        $remaining = max(0, $maxRequests - ($rateLimit->counter + 1));
    70â†’        $this->addRateLimitHeaders($maxRequests, $remaining, strtotime($rateLimit->window_end));
    71â†’
    72â†’        return true;
    73â†’    }
    74â†’
    75â†’    /**
    76â†’     * Add rate limit headers to the response
    77â†’     */
    78â†’    public function addRateLimitHeaders(?int $limit = null, ?int $remaining = null, ?int $reset = null, ?int $retryAfter = null): void
    79â†’    {
    80â†’        // Allow short-circuiting the rate limit check, check the settings first and then honor the filter
    81â†’        $rateLimitEnabled = apply_filters('jwt_auth_rate_limit_enabled', $this->settingsService->getSetting('rate_limit_settings', 'enabled'));
    82â†’        $sendHeaders = apply_filters('jwt_auth_rate_limit_headers_enabled', true);
    83â†’
    84â†’        if (!$rateLimitEnabled || !$sendHeaders) {
    85â†’            return;
    86â†’        }
    87â†’
    88â†’        if (!function_exists('header')) {
    89â†’            return;
    90â†’        }
    91â†’
    92â†’        // If we dont have a limit, remaining or reset value, we need to get the data from the RateLimitRepository
    93â†’        if ($limit === null && $remaining === null && $reset === null) {
    94â†’            // Get the data from the RateLimitRepository
    95â†’            $rateLimit = $this->repository->findByIpAddress($this->getRequestIP());
    96â†’            // Now calculate the limit, remaining and reset values based on the $rateLimit data
    97â†’            $limit = $this->getRateLimitRequests();
    98â†’            $remaining = isset($rateLimit->counter) ? $limit - $rateLimit->counter : $limit;
    99â†’            $reset = isset($rateLimit->window_end) ? strtotime($rateLimit->window_end) : strtotime('+' . $this->getRateLimitWindowMinutes() . ' minutes');
   100â†’            $retryAfter = isset($rateLimit->window_end) ? strtotime($rateLimit->window_end) - time() : null;
   101â†’        }
   102â†’
   103â†’        header('X-RateLimit-Limit: ' . $limit);
   104â†’        header('X-RateLimit-Remaining: ' . $remaining);
   105â†’        header('X-RateLimit-Reset: ' . $reset);
   106â†’
   107â†’        if ($retryAfter !== null) {
   108â†’            header('Retry-After: ' . $retryAfter);
   109â†’        }
   110â†’    }
   111â†’
   112â†’    private function getRateLimitRequests(): int
   113â†’    {
   114â†’        return (int) apply_filters(
   115â†’            'jwt_auth_rate_limit_max_requests',
   116â†’            $this->settingsService->getSetting('rate_limit_settings', 'max_requests')
   117â†’        );
   118â†’    }
   119â†’
   120â†’    private function getRateLimitWindowMinutes(): int
   121â†’    {
   122â†’        return (int) apply_filters(
   123â†’            'jwt_auth_rate_limit_window_minutes',
   124â†’            $this->settingsService->getSetting('rate_limit_settings', 'window_minutes')
   125â†’        );
   126â†’    }
   127â†’}
