export interface DataManagementSettings {
  delete_on_deactivation: boolean;
  analytics_retention_days: number;
  security_events_retention_days: number;
  anonymize_ip: boolean;
}

export interface TokenSettings {
  jwt_expiration: number;
  refresh_token_expiration: number;
  signing_algorithm: 'HS256' | string; // We can expand this enum based on JWT::$supportedAlgorithms
  enable_cors: boolean;
}

export interface UserSettings {
  revoke_on_password_change: boolean;
  revoke_on_role_change: boolean;
  revoke_on_email_change: boolean;
  delete_on_user_delete: boolean;
}

export interface RateLimitSettings {
  max_requests: number;
  window_minutes: number;
  enabled: boolean;
}

export interface Settings {
  data_management: DataManagementSettings;
  token_settings: TokenSettings;
  user_settings: UserSettings;
  rate_limit_settings: RateLimitSettings;
}

export type SettingsGroup = keyof Settings;
export type SettingKey<T extends SettingsGroup> = keyof Settings[T];

