{
  "name": "tmeister/jwt-auth-pro",
  "description": "Professional JWT Authentication for WordPress REST API",
  "type": "wordpress-plugin",
  "license": "GPL-2.0-or-later",
  "version": "0.1.0",
  "authors": [
    {
      "name": "Enrique Chavez",
      "email": "noone@tmeister.net"
    }
  ],
  "require": {
    "php": "^8.1",
    "firebase/php-jwt": "^6.11",
    "freemius/wordpress-sdk": "^2.11",
    "geoip2/geoip2": "^3.1"
  },
  "require-dev": {
    "brain/monkey": "^2.6",
    "friendsofphp/php-cs-fixer": "^3.65",
    "mockery/mockery": "^1.5",
    "php-stubs/wordpress-tests-stubs": "^6.6",
    "phpunit/phpunit": "^9.5",
    "szepeviktor/phpstan-wordpress": "^1.3",
    "wp-phpunit/wp-phpunit": "^6.7",
    "yoast/phpunit-polyfills": "^3.0"
  },
  "autoload": {
    "psr-4": {
      "Tmeister\\JWTAuthPro\\": "src/"
    }
  },
  "autoload-dev": {
    "psr-4": {
      "Tmeister\\JWTAuthPro\\Tests\\": "tests/"
    }
  },
  "config": {
    "sort-packages": true
  },
  "scripts": {
    "cs-fix": "php-cs-fixer fix",
    "cs-check": "php-cs-fixer fix --dry-run --diff",
    "analyse": "phpstan analyse --memory-limit=1G",
    "test": "phpunit",
    "test-coverage": "phpunit --coverage-html coverage",
    "check": ["@cs-check", "@analyse", "@test"]
  }
}

{
  "version": "1",
  "name": "jwt-auth-pro",
  "type": "collection",
  "ignore": ["node_modules", ".git"],
  "presets": {
    "requestType": "http",
    "requestUrl": "http://jwt.test"
  }
}

