import { http, HttpResponse } from 'msw';
import { mockTokens, mockTokenDetails, mockAnalytics, mockSettings, mockSystemInfo } from './data';

const API_BASE = 'http://localhost:8888/wp-json/jwt-auth/v1';

export const handlers = [
  // Analytics endpoints
  http.get(`${API_BASE}/analytics/summary`, () => {
    return HttpResponse.json({
      success: true,
      data: mockAnalytics.summary
    });
  }),

  http.get(`${API_BASE}/analytics/all-time`, () => {
    return HttpResponse.json({
      success: true,
      data: mockAnalytics.allTimeStats
    });
  }),

  http.get(`${API_BASE}/analytics/historical`, ({ request }) => {
    const url = new URL(request.url);
    const period = url.searchParams.get('period') || '7d';
    return HttpResponse.json({
      success: true,
      data: mockAnalytics.historical[period as keyof typeof mockAnalytics.historical]
    });
  }),

  // Token endpoints
  http.get(`${API_BASE}/admin/tokens`, ({ request }) => {
    const url = new URL(request.url);
    const page = parseInt(url.searchParams.get('page') || '1', 10);
    const perPage = parseInt(url.searchParams.get('per_page') || '10', 10);
    const status = url.searchParams.get('status');
    const username = url.searchParams.get('username');

    let filteredTokens = [...mockTokens.items];

    // Apply filters
    if (status && status !== 'all') {
      filteredTokens = filteredTokens.filter(token => token.status === status);
    }
    if (username) {
      filteredTokens = filteredTokens.filter(token => 
        token.user.login.toLowerCase().includes(username.toLowerCase())
      );
    }

    // Apply pagination
    const startIndex = (page - 1) * perPage;
    const endIndex = startIndex + perPage;
    const paginatedTokens = filteredTokens.slice(startIndex, endIndex);

    return HttpResponse.json({
      success: true,
      data: {
        items: paginatedTokens,
        pagination: {
          current_page: page,
          per_page: perPage,
          total_items: filteredTokens.length,
          total_pages: Math.ceil(filteredTokens.length / perPage)
        }
      }
    });
  }),

  http.get(`${API_BASE}/admin/tokens/:id`, ({ params }) => {
    const id = parseInt(params.id as string, 10);
    const token = mockTokens.items.find(t => t.id === id);

    if (!token) {
      return HttpResponse.json(
        { success: false, message: 'Token not found' },
        { status: 404 }
      );
    }

    return HttpResponse.json({
      success: true,
      data: token
    });
  }),

  http.get(`${API_BASE}/admin/tokens/:id/details`, ({ params }) => {
    const id = parseInt(params.id as string, 10);
    const details = mockTokenDetails[id];

    if (!details) {
      return HttpResponse.json(
        { success: false, message: 'Token details not found' },
        { status: 404 }
      );
    }

    return HttpResponse.json({
      success: true,
      data: details
    });
  }),

  http.put(`${API_BASE}/admin/tokens/:id`, ({ params }) => {
    const id = parseInt(params.id as string, 10);
    const token = mockTokens.items.find(t => t.id === id);

    if (!token) {
      return HttpResponse.json(
        { success: false, message: 'Token not found' },
        { status: 404 }
      );
    }

    // Simulate revocation
    const revokedToken = { ...token, status: 'revoked' as const };
    return HttpResponse.json({
      success: true,
      data: revokedToken
    });
  }),

  http.delete(`${API_BASE}/admin/tokens/:id`, ({ params }) => {
    const id = parseInt(params.id as string, 10);
    const tokenExists = mockTokens.items.some(t => t.id === id);

    if (!tokenExists) {
      return HttpResponse.json(
        { success: false, message: 'Token not found' },
        { status: 404 }
      );
    }

    return HttpResponse.json({
      success: true,
      message: 'Token deleted successfully'
    });
  }),

  http.post(`${API_BASE}/admin/tokens/bulk`, async ({ request }) => {
    const body = await request.json() as { action: string; token_ids: number[] };
    const { action, token_ids } = body;

    if (!['revoke', 'delete'].includes(action)) {
      return HttpResponse.json(
        { success: false, message: 'Invalid action' },
        { status: 400 }
      );
    }

    // Simulate bulk action success
    return HttpResponse.json({
      success: true,
      data: {
        success: token_ids,
        failed: []
      }
    });
  }),

  // Settings endpoints
  http.get('http://localhost:8888/wp-json/wp/v2/settings', () => {
    return HttpResponse.json({
      jwt_auth_pro_settings: mockSettings.current
    });
  }),

  http.post('http://localhost:8888/wp-json/wp/v2/settings', async ({ request }) => {
    const body = await request.json() as { jwt_auth_pro_settings: any };
    return HttpResponse.json({
      jwt_auth_pro_settings: body.jwt_auth_pro_settings
    });
  }),

  http.get(`${API_BASE}/admin/settings/options`, () => {
    return HttpResponse.json({
      success: true,
      data: mockSettings.options
    });
  }),

  // System endpoints
  http.get(`${API_BASE}/admin/system/info`, () => {
    return HttpResponse.json({
      success: true,
      data: mockSystemInfo
    });
  }),

  // Error simulation handlers
  http.get(`${API_BASE}/error/500`, () => {
    return HttpResponse.json(
      { success: false, message: 'Internal server error' },
      { status: 500 }
    );
  }),

  http.get(`${API_BASE}/error/404`, () => {
    return HttpResponse.json(
      { success: false, message: 'Not found' },
      { status: 404 }
    );
  }),

  http.get(`${API_BASE}/error/network`, () => {
    return HttpResponse.error();
  })
];

