import { describe, it, expect, beforeEach, vi } from 'vitest';
import { screen, waitFor } from '@testing-library/react';
import { render, userEvent } from '../../utils/test-utils';
import { TokensTable } from '@/components/tokens/tokens-table';
import { server } from '../../mocks/server';
import { http, HttpResponse } from 'msw';

const mockFilters = {
  status: 'active' as const,
  username: '',
  creationDate: undefined,
  expireDate: undefined,
  revokedDate: undefined
};

describe('TokensTable', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  it('renders loading state initially', () => {
    render(<TokensTable filters={mockFilters} />);
    expect(screen.getByText('Loading...')).toBeInTheDocument();
  });

  it('renders tokens table with data', async () => {
    render(<TokensTable filters={mockFilters} />);

    await waitFor(() => {
      expect(screen.getByText('admin')).toBeInTheDocument();
      expect(screen.getByText('editor')).toBeInTheDocument();
    });

    // Check table headers
    expect(screen.getByText('User')).toBeInTheDocument();
    expect(screen.getByText('Status')).toBeInTheDocument();
    expect(screen.getByText('Created')).toBeInTheDocument();
    expect(screen.getByText('Expires')).toBeInTheDocument();
    expect(screen.getByText('Last Used')).toBeInTheDocument();
  });

  it('displays correct status badges', async () => {
    render(<TokensTable filters={mockFilters} />);

    await waitFor(() => {
      expect(screen.getByText('Active')).toBeInTheDocument();
      expect(screen.getByText('Expired')).toBeInTheDocument();
      expect(screen.getByText('Revoked')).toBeInTheDocument();
    });
  });

  it('handles pagination correctly', async () => {
    render(<TokensTable filters={mockFilters} />);

    await waitFor(() => {
      expect(screen.getByText('Showing 1 to 3 of 3 results')).toBeInTheDocument();
    });
  });

  it('filters tokens by status', async () => {
    const filters = { ...mockFilters, status: 'expired' as const };
    render(<TokensTable filters={filters} />);

    await waitFor(() => {
      expect(screen.getByText('editor')).toBeInTheDocument();
      expect(screen.queryByText('admin')).not.toBeInTheDocument();
    });
  });

  it('filters tokens by username', async () => {
    const filters = { ...mockFilters, username: 'admin' };
    render(<TokensTable filters={filters} />);

    await waitFor(() => {
      expect(screen.getByText('admin')).toBeInTheDocument();
      expect(screen.queryByText('editor')).not.toBeInTheDocument();
    });
  });

  it('handles bulk selection', async () => {
    const user = userEvent.setup();
    render(<TokensTable filters={mockFilters} />);

    await waitFor(() => {
      expect(screen.getByText('admin')).toBeInTheDocument();
    });

    // Find and click the first checkbox (should be for admin user)
    const checkboxes = screen.getAllByRole('checkbox');
    const firstItemCheckbox = checkboxes[1]; // Skip the header checkbox

    await user.click(firstItemCheckbox);

    // Should show bulk actions
    expect(screen.getByText('1 token selected')).toBeInTheDocument();
    expect(screen.getByText('Revoke Selected')).toBeInTheDocument();
    expect(screen.getByText('Delete Selected')).toBeInTheDocument();
  });

  it('performs bulk revoke action', async () => {
    const user = userEvent.setup();
    render(<TokensTable filters={mockFilters} />);

    await waitFor(() => {
      expect(screen.getByText('admin')).toBeInTheDocument();
    });

    // Select token
    const checkboxes = screen.getAllByRole('checkbox');
    const firstItemCheckbox = checkboxes[1];
    await user.click(firstItemCheckbox);

    // Click revoke button
    const revokeButton = screen.getByText('Revoke Selected');
    await user.click(revokeButton);

    // Confirm in dialog
    await waitFor(() => {
      expect(screen.getByText('Revoke Tokens')).toBeInTheDocument();
    });

    const confirmButton = screen.getByRole('button', { name: /revoke/i });
    await user.click(confirmButton);

    // Check for success message
    await waitFor(() => {
      expect(screen.getByText('1 token(s) revoked successfully')).toBeInTheDocument();
    });
  });

  it('handles individual token revocation', async () => {
    const user = userEvent.setup();
    render(<TokensTable filters={mockFilters} />);

    await waitFor(() => {
      expect(screen.getByText('admin')).toBeInTheDocument();
    });

    // Find and click the actions button for the first token
    const actionsButtons = screen.getAllByText('Actions');
    await user.click(actionsButtons[0]);

    // Click revoke option
    const revokeOption = screen.getByText('Revoke');
    await user.click(revokeOption);

    // Confirm in dialog
    await waitFor(() => {
      expect(screen.getByText('Revoke Token')).toBeInTheDocument();
    });

    const confirmButton = screen.getByRole('button', { name: /revoke/i });
    await user.click(confirmButton);

    // Check for success message
    await waitFor(() => {
      expect(screen.getByText('Token revoked successfully')).toBeInTheDocument();
    });
  });

  it('handles API errors gracefully', async () => {
    // Mock API error
    server.use(
      http.get('http://localhost:8888/wp-json/jwt-auth/v1/admin/tokens', () => {
        return HttpResponse.json(
          { success: false, message: 'Server error' },
          { status: 500 }
        );
      })
    );

    render(<TokensTable filters={mockFilters} />);

    await waitFor(() => {
      expect(screen.getByText('Failed to load tokens')).toBeInTheDocument();
    });
  });

  it('shows empty state when no tokens', async () => {
    // Mock empty response
    server.use(
      http.get('http://localhost:8888/wp-json/jwt-auth/v1/admin/tokens', () => {
        return HttpResponse.json({
          success: true,
          data: {
            items: [],
            pagination: {
              current_page: 1,
              per_page: 10,
              total_items: 0,
              total_pages: 0
            }
          }
        });
      })
    );

    render(<TokensTable filters={mockFilters} />);

    await waitFor(() => {
      expect(screen.getByText('No tokens found')).toBeInTheDocument();
    });
  });

  it('navigates to token details page', async () => {
    const user = userEvent.setup();
    render(<TokensTable filters={mockFilters} />);

    await waitFor(() => {
      expect(screen.getByText('admin')).toBeInTheDocument();
    });

    // Find and click the actions button for the first token
    const actionsButtons = screen.getAllByText('Actions');
    await user.click(actionsButtons[0]);

    // Click view details option
    const viewDetailsOption = screen.getByText('View Details');
    await user.click(viewDetailsOption);

    // Should navigate to details page (would need router mock to fully test)
    // For now, just verify the action exists
    expect(viewDetailsOption).toBeInTheDocument();
  });

  it('handles date filtering correctly', async () => {
    const creationDate = new Date('2024-01-15');
    const filters = { ...mockFilters, creationDate };

    render(<TokensTable filters={filters} />);

    await waitFor(() => {
      // Should apply date filter to API call
      expect(screen.getByText('admin')).toBeInTheDocument();
    });
  });
});

