import { describe, it, expect, vi } from 'vitest';
import { screen, waitFor } from '@testing-library/react';
import { render, userEvent } from '../../utils/test-utils';
import { AnalyticsCharts } from '@/components/analytics/analytics-charts';
import { mockAnalytics } from '../../mocks/data';

const mockHistoricalData = mockAnalytics.historical['7d'];

const defaultProps = {
  historicalData: mockHistoricalData,
  defaultPeriod: '7d' as const,
  onPeriodChange: vi.fn()
};

describe('AnalyticsCharts', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  it('renders analytics charts with data', () => {
    render(<AnalyticsCharts {...defaultProps} />);

    expect(screen.getByText('Token Activity Overview')).toBeInTheDocument();
    expect(screen.getByText('User Activity')).toBeInTheDocument();
  });

  it('displays period selector with correct options', () => {
    render(<AnalyticsCharts {...defaultProps} />);

    expect(screen.getByText('7 days')).toBeInTheDocument();

    // Should have period selector
    const periodSelector = screen.getByRole('combobox');
    expect(periodSelector).toBeInTheDocument();
  });

  it('changes period when selector is used', async () => {
    const user = userEvent.setup();
    const onPeriodChange = vi.fn();

    render(
      <AnalyticsCharts 
        {...defaultProps} 
        onPeriodChange={onPeriodChange} 
      />
    );

    // Click period selector
    const periodSelector = screen.getByRole('combobox');
    await user.click(periodSelector);

    // Select 30 days option
    const thirtyDaysOption = screen.getByText('30 days');
    await user.click(thirtyDaysOption);

    expect(onPeriodChange).toHaveBeenCalledWith('30d');
  });

  it('renders chart tooltips on hover', async () => {
    const user = userEvent.setup();
    render(<AnalyticsCharts {...defaultProps} />);

    // Find chart areas (Recharts creates SVG elements)
    const chartContainer = screen.getByText('Token Activity Overview').closest('[data-testid]') || 
                          document.querySelector('.recharts-wrapper');

    if (chartContainer) {
      // Hover over chart area
      await user.hover(chartContainer);

      // Should show tooltip with data
      await waitFor(() => {
        // Recharts tooltips contain the data values
        expect(screen.getByText(/Generated/)).toBeInTheDocument();
      });
    }
  });

  it('displays correct chart legends', () => {
    render(<AnalyticsCharts {...defaultProps} />);

    // Check for legend items
    expect(screen.getByText('Generated')).toBeInTheDocument();
    expect(screen.getByText('Validated')).toBeInTheDocument();
    expect(screen.getByText('Refreshed')).toBeInTheDocument();
    expect(screen.getByText('Unique Users')).toBeInTheDocument();
  });

  it('handles empty data gracefully', () => {
    render(
      <AnalyticsCharts 
        {...defaultProps} 
        historicalData={[]} 
      />
    );

    // Should still render chart containers
    expect(screen.getByText('Token Activity Overview')).toBeInTheDocument();
    expect(screen.getByText('User Activity')).toBeInTheDocument();
  });

  it('formats dates correctly on x-axis', () => {
    render(<AnalyticsCharts {...defaultProps} />);

    // Check that dates are displayed (Recharts formats them)
    // The exact format depends on the chart configuration
    expect(screen.getByText('Token Activity Overview')).toBeInTheDocument();
  });

  it('shows different chart types for different metrics', () => {
    render(<AnalyticsCharts {...defaultProps} />);

    // Should have both line charts for different metric types
    const chartSections = screen.getAllByText(/Overview|Activity/);
    expect(chartSections).toHaveLength(2);
  });

  it('updates charts when historical data changes', () => {
    const { rerender } = render(<AnalyticsCharts {...defaultProps} />);

    // Initial data
    expect(screen.getByText('Token Activity Overview')).toBeInTheDocument();

    // New data with different period
    const newData = mockAnalytics.historical['30d'].slice(0, 7);
    rerender(
      <AnalyticsCharts 
        {...defaultProps} 
        historicalData={newData}
        defaultPeriod="30d"
      />
    );

    // Should update the charts with new data
    expect(screen.getByText('Token Activity Overview')).toBeInTheDocument();
  });

  it('maintains responsive design', () => {
    render(<AnalyticsCharts {...defaultProps} />);

    // Check for responsive chart containers
    const chartContainers = document.querySelectorAll('.recharts-responsive-container');
    expect(chartContainers.length).toBeGreaterThan(0);
  });
});

