import { screen, waitFor } from '@testing-library/react';
import { render, userEvent } from '../../utils/test-utils';
import SettingsPage from '@/pages/settings/index';
import { server } from '../../mocks/server';
import { http, HttpResponse } from 'msw';

describe('SettingsPage', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  it('renders settings page header', () => {
    render(<SettingsPage />);

    expect(screen.getByText('Configuration Settings')).toBeInTheDocument();
    expect(screen.getByText(/Configure your JWT authentication settings/)).toBeInTheDocument();
  });

  it('displays loading state initially', () => {
    render(<SettingsPage />);

    expect(screen.getByText('Loading...')).toBeInTheDocument();
  });

  it('loads and displays settings data', async () => {
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Data Management')).toBeInTheDocument();
      expect(screen.getByText('Token Settings')).toBeInTheDocument();
      expect(screen.getByText('Security Actions')).toBeInTheDocument();
      expect(screen.getByText('Rate Limiting')).toBeInTheDocument();
    });
  });

  it('displays data management settings correctly', async () => {
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Delete data on deactivation')).toBeInTheDocument();
      expect(screen.getByText('Analytics Retention')).toBeInTheDocument();
      expect(screen.getByText('Anonymize IP Addresses')).toBeInTheDocument();
    });

    // Check default values
    const deleteOnDeactivationSwitch = screen.getByRole('switch', { name: /Delete data on deactivation/i });
    expect(deleteOnDeactivationSwitch).not.toBeChecked();

    const anonymizeIpSwitch = screen.getByRole('switch', { name: /Anonymize IP Addresses/i });
    expect(anonymizeIpSwitch).toBeChecked();
  });

  it('displays token settings correctly', async () => {
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('JWT Token Expiration')).toBeInTheDocument();
      expect(screen.getByText('Refresh Token Expiration')).toBeInTheDocument();
      expect(screen.getByText('Signing Algorithm')).toBeInTheDocument();
      expect(screen.getByText('Enable CORS')).toBeInTheDocument();
    });

    // Check CORS is enabled by default
    const corsSwitch = screen.getByRole('switch', { name: /Enable CORS/i });
    expect(corsSwitch).toBeChecked();
  });

  it('displays security actions correctly', async () => {
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Revoke on Password Change')).toBeInTheDocument();
      expect(screen.getByText('Revoke on Role Change')).toBeInTheDocument();
      expect(screen.getByText('Revoke on Email Change')).toBeInTheDocument();
      expect(screen.getByText('Delete on User Delete')).toBeInTheDocument();
    });

    // Check default security settings
    const passwordChangeSwitch = screen.getByRole('switch', { name: /Revoke on Password Change/i });
    expect(passwordChangeSwitch).toBeChecked();

    const roleChangeSwitch = screen.getByRole('switch', { name: /Revoke on Role Change/i });
    expect(roleChangeSwitch).toBeChecked();

    const emailChangeSwitch = screen.getByRole('switch', { name: /Revoke on Email Change/i });
    expect(emailChangeSwitch).not.toBeChecked();
  });

  it('displays rate limiting settings correctly', async () => {
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Enable Rate Limiting')).toBeInTheDocument();
      expect(screen.getByText('Maximum Requests')).toBeInTheDocument();
      expect(screen.getByText('Time Window')).toBeInTheDocument();
    });

    // Check rate limiting is enabled
    const rateLimitSwitch = screen.getByRole('switch', { name: /Enable Rate Limiting/i });
    expect(rateLimitSwitch).toBeChecked();

    // Check default values
    const maxRequestsInput = screen.getByDisplayValue('60');
    expect(maxRequestsInput).toBeInTheDocument();

    const timeWindowInput = screen.getByDisplayValue('1');
    expect(timeWindowInput).toBeInTheDocument();
  });

  it('handles switch toggle interactions', async () => {
    const user = userEvent.setup();
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Data Management')).toBeInTheDocument();
    });

    // Toggle delete on deactivation
    const deleteOnDeactivationSwitch = screen.getByRole('switch', { name: /Delete data on deactivation/i });
    await user.click(deleteOnDeactivationSwitch);

    expect(deleteOnDeactivationSwitch).toBeChecked();
  });

  it('handles dropdown selections', async () => {
    const user = userEvent.setup();
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Analytics Retention')).toBeInTheDocument();
    });

    // Find analytics retention dropdown
    const retentionDropdown = screen.getByDisplayValue('90 days');
    await user.click(retentionDropdown);

    // Select different option
    const option180Days = screen.getByText('180 days');
    await user.click(option180Days);

    // Should update the dropdown value
    await waitFor(() => {
      expect(screen.getByDisplayValue('180 days')).toBeInTheDocument();
    });
  });

  it('handles number input changes', async () => {
    const user = userEvent.setup();
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Rate Limiting')).toBeInTheDocument();
    });

    // Change max requests value
    const maxRequestsInput = screen.getByDisplayValue('60');
    await user.clear(maxRequestsInput);
    await user.type(maxRequestsInput, '100');

    expect(maxRequestsInput).toHaveValue(100);
  });

  it('saves settings successfully', async () => {
    const user = userEvent.setup();
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Save Changes')).toBeInTheDocument();
    });

    // Make a change
    const deleteOnDeactivationSwitch = screen.getByRole('switch', { name: /Delete data on deactivation/i });
    await user.click(deleteOnDeactivationSwitch);

    // Save changes
    const saveButton = screen.getByText('Save Changes');
    await user.click(saveButton);

    // Should show saving state
    expect(screen.getByText('Saving...')).toBeInTheDocument();

    // Should show success message
    await waitFor(() => {
      expect(screen.getByText('Settings saved successfully.')).toBeInTheDocument();
    });

    // Button should return to normal state
    await waitFor(() => {
      expect(screen.getByText('Save Changes')).toBeInTheDocument();
    });
  });

  it('handles save errors gracefully', async () => {
    const user = userEvent.setup();

    // Mock save error
    server.use(
      http.post('http://localhost:8888/wp-json/wp/v2/settings', () => {
        return HttpResponse.json(
          { success: false, message: 'Save failed' },
          { status: 500 }
        );
      })
    );

    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Save Changes')).toBeInTheDocument();
    });

    // Try to save
    const saveButton = screen.getByText('Save Changes');
    await user.click(saveButton);

    // Should show error message
    await waitFor(() => {
      expect(screen.getByText('Failed to save settings. Please try again.')).toBeInTheDocument();
    });
  });

  it('handles load error gracefully', async () => {
    // Mock load error
    server.use(
      http.get('http://localhost:8888/wp-json/wp/v2/settings', () => {
        return HttpResponse.json(
          { success: false, message: 'Load failed' },
          { status: 500 }
        );
      })
    );

    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Failed to load settings. Please try again.')).toBeInTheDocument();
    });
  });

  it('validates number inputs correctly', async () => {
    const user = userEvent.setup();
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Rate Limiting')).toBeInTheDocument();
    });

    // Try invalid input
    const maxRequestsInput = screen.getByDisplayValue('60');
    await user.clear(maxRequestsInput);
    await user.type(maxRequestsInput, 'invalid');

    // Should fall back to default value when saved
    const saveButton = screen.getByText('Save Changes');
    await user.click(saveButton);

    // Should use fallback value (60)
    await waitFor(() => {
      expect(maxRequestsInput).toHaveValue(60);
    });
  });

  it('displays correct option values in dropdowns', async () => {
    const user = userEvent.setup();
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Token Settings')).toBeInTheDocument();
    });

    // Check JWT expiration dropdown
    const jwtExpirationDropdown = screen.getByDisplayValue('7 days');
    await user.click(jwtExpirationDropdown);

    // Should show all available options
    expect(screen.getByText('1 day')).toBeInTheDocument();
    expect(screen.getByText('14 days')).toBeInTheDocument();
    expect(screen.getByText('30 days')).toBeInTheDocument();
    expect(screen.getByText('90 days')).toBeInTheDocument();
  });
});

