    const maxRequestsInput = screen.getByDisplayValue('60');
    await user.clear(maxRequestsInput);
    await user.type(maxRequestsInput, '100');

    expect(maxRequestsInput).toHaveValue(100);
  });

  it('saves settings successfully', async () => {
    const user = userEvent.setup();
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Save Changes')).toBeInTheDocument();
    });

    // Make a change
    const deleteOnDeactivationSwitch = screen.getByRole('switch', { name: /Delete data on deactivation/i });
    await user.click(deleteOnDeactivationSwitch);

    // Save changes
    const saveButton = screen.getByText('Save Changes');
    await user.click(saveButton);

    // Should show saving state
    expect(screen.getByText('Saving...')).toBeInTheDocument();

    // Should show success message
    await waitFor(() => {
      expect(screen.getByText('Settings saved successfully.')).toBeInTheDocument();
    });

    // Button should return to normal state
    await waitFor(() => {
      expect(screen.getByText('Save Changes')).toBeInTheDocument();
    });
  });

  it('handles save errors gracefully', async () => {
    const user = userEvent.setup();

    // Mock save error
    server.use(
      http.post('http://localhost:8888/wp-json/wp/v2/settings', () => {
        return HttpResponse.json(
          { success: false, message: 'Save failed' },
          { status: 500 }
        );
      })
    );

    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Save Changes')).toBeInTheDocument();
    });

    // Try to save
    const saveButton = screen.getByText('Save Changes');
    await user.click(saveButton);

    // Should show error message
    await waitFor(() => {
      expect(screen.getByText('Failed to save settings. Please try again.')).toBeInTheDocument();
    });
  });

  it('handles load error gracefully', async () => {
    // Mock load error
    server.use(
      http.get('http://localhost:8888/wp-json/wp/v2/settings', () => {
        return HttpResponse.json(
          { success: false, message: 'Load failed' },
          { status: 500 }
        );
      })
    );

    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Failed to load settings. Please try again.')).toBeInTheDocument();
    });
  });

  it('validates number inputs correctly', async () => {
    const user = userEvent.setup();
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Rate Limiting')).toBeInTheDocument();
    });

    // Try invalid input
    const maxRequestsInput = screen.getByDisplayValue('60');
    await user.clear(maxRequestsInput);
    await user.type(maxRequestsInput, 'invalid');

    // Should fall back to default value when saved
    const saveButton = screen.getByText('Save Changes');
    await user.click(saveButton);

    // Should use fallback value (60)
    await waitFor(() => {
      expect(maxRequestsInput).toHaveValue(60);
    });
  });

  it('displays correct option values in dropdowns', async () => {
    const user = userEvent.setup();
    render(<SettingsPage />);

    await waitFor(() => {
      expect(screen.getByText('Token Settings')).toBeInTheDocument();
    });

    // Check JWT expiration dropdown
    const jwtExpirationDropdown = screen.getByDisplayValue('7 days');
    await user.click(jwtExpirationDropdown);

    // Should show all available options
    expect(screen.getByText('1 day')).toBeInTheDocument();
    expect(screen.getByText('14 days')).toBeInTheDocument();
    expect(screen.getByText('30 days')).toBeInTheDocument();
    expect(screen.getByText('90 days')).toBeInTheDocument();
  });
