    await waitFor(() => {
      expect(screen.getByText('Failed to copy system information')).toBeInTheDocument();
    });
  });

  it('handles API loading errors gracefully', async () => {
    // Mock API error
    server.use(
      http.get('http://localhost:8888/wp-json/jwt-auth/v1/admin/system/info', () => {
        return HttpResponse.json(
          { success: false, message: 'Server error' },
          { status: 500 }
        );
      })
    );

    render(<SystemPage />);

    // Should still render the page structure
    expect(screen.getByText('System Status & Information')).toBeInTheDocument();

    // Copy button should remain disabled
    await waitFor(() => {
      const copyButton = screen.getByText('Copy System Info');
      expect(copyButton).toBeDisabled();
    });
  });

  it('displays status indicators correctly', async () => {
    render(<SystemPage />);

    await waitFor(() => {
      // Should show optimal status for versions
      const statusElements = screen.getAllByText('Optimal');
      expect(statusElements.length).toBeGreaterThan(0);
    });
  });

  it('formats boolean values correctly', async () => {
    render(<SystemPage />);

    await waitFor(() => {
      // Multisite should show as "No"
      expect(screen.getByText('No')).toBeInTheDocument();

      // SSL should show as "Installed" 
      expect(screen.getByText('Installed')).toBeInTheDocument();

      // Debug constants should show as "Disabled"
      const disabledElements = screen.getAllByText('Disabled');
      expect(disabledElements.length).toBeGreaterThan(0);
    });
  });

  it('includes all required information in copied text', async () => {
    const user = userEvent.setup();
    mockWriteText.mockResolvedValue(undefined);

    render(<SystemPage />);

    await waitFor(() => {
      expect(screen.getByText('Copy System Info')).not.toBeDisabled();
    });

    const copyButton = screen.getByText('Copy System Info');
    await user.click(copyButton);

    const copiedText = mockWriteText.mock.calls[0][0];

    // Should include all major sections
    expect(copiedText).toContain('WordPress Environment:');
    expect(copiedText).toContain('Server Environment:');
    expect(copiedText).toContain('PHP Configuration:');
    expect(copiedText).toContain('WordPress Constants:');
    expect(copiedText).toContain('Active Theme:');
    expect(copiedText).toContain('Active Plugins:');
    expect(copiedText).toContain('Inactive Plugins:');
  });

  it('shows loading state while fetching system info', () => {
    render(<SystemPage />);

    // Initially should be loading
    const copyButton = screen.getByText('Copy System Info');
    expect(copyButton).toBeDisabled();
  });
});

