import { describe, it, expect, beforeEach, vi } from 'vitest';
import { analyticsAPI, tokenAPI, settingsAPI, systemAPI } from '@/services/api';
import { server } from '../mocks/server';
import { http, HttpResponse } from 'msw';

// Mock window.wpApiSettings
const mockWpApiSettings = {
  nonce: 'test-nonce-12345',
  root: 'http://localhost:8888/wp-json/',
  versionString: 'wp/v2/'
};

Object.defineProperty(window, 'wpApiSettings', {
  value: mockWpApiSettings,
  writable: true
});

describe('API Services', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  describe('analyticsAPI', () => {
    it('fetches dashboard summary successfully', async () => {
      const result = await analyticsAPI.getDashboardSummary();

      expect(result).toEqual({
        total_tokens: 156,
        active_tokens: 89,
        expired_tokens: 45,
        revoked_tokens: 22,
        total_requests: 12453,
        successful_requests: 11876,
        failed_requests: 577,
        unique_users: 34
      });
    });

    it('fetches all-time stats successfully', async () => {
      const result = await analyticsAPI.getAllTimeStats();

      expect(result).toEqual({
        tokens: {
          total: 156,
          active: 89,
          expired: 45,
          revoked: 22
        },
        events: {
          total: 12453,
          successful: 11876,
          failed: 577
        },
        users: {
          unique: 34,
          active_last_30_days: 28
        }
      });
    });

    it('fetches historical data with default period', async () => {
      const result = await analyticsAPI.getHistoricalData();

      expect(result).toBeInstanceOf(Array);
      expect(result.length).toBeGreaterThan(0);
      expect(result[0]).toHaveProperty('date');
      expect(result[0]).toHaveProperty('tokens_generated');
    });

    it('fetches historical data with custom period', async () => {
      const result = await analyticsAPI.getHistoricalData('30d');

      expect(result).toBeInstanceOf(Array);
      expect(result.length).toBe(30);
    });

    it('handles analytics API errors', async () => {
      server.use(
        http.get('http://localhost:8888/wp-json/jwt-auth/v1/analytics/summary', () => {
          return HttpResponse.json(
            { success: false, message: 'Server error' },
            { status: 500 }
          );
        })
      );

      await expect(analyticsAPI.getDashboardSummary()).rejects.toThrow();
    });
  });

  describe('tokenAPI', () => {
    it('lists tokens with default parameters', async () => {
      const result = await tokenAPI.list({});

      expect(result).toHaveProperty('items');
      expect(result).toHaveProperty('pagination');
      expect(result.items).toBeInstanceOf(Array);
      expect(result.items.length).toBe(3);
    });

    it('lists tokens with filters', async () => {
      const result = await tokenAPI.list({
        status: 'active',
        username: 'admin',
        page: 1,
        per_page: 10
      });

      expect(result.items).toBeInstanceOf(Array);
      expect(result.items.length).toBe(1);
      expect(result.items[0].user.login).toBe('admin');
    });

    it('fetches single token by ID', async () => {
      const result = await tokenAPI.get(1);

      expect(result).toHaveProperty('id', 1);
      expect(result).toHaveProperty('user');
      expect(result).toHaveProperty('status');
    });

    it('handles token not found error', async () => {
      await expect(tokenAPI.get(999)).rejects.toThrow('HTTP error! status: 404');
    });

    it('revokes token successfully', async () => {
      const result = await tokenAPI.revoke(1);

      expect(result).toHaveProperty('status', 'revoked');
    });

    it('deletes token successfully', async () => {
      const result = await tokenAPI.delete(1);

