import React, { ReactElement, ReactNode } from 'react';
import { render, RenderOptions } from '@testing-library/react';
import { MemoryRouter } from 'react-router-dom';
import { TabContext } from '@/App';
import { ToastProvider } from '@/components/providers/toast-provider';

interface CustomRenderOptions extends Omit<RenderOptions, 'wrapper'> {
  initialEntries?: string[];
  initialTab?: string;
}

interface AllTheProvidersProps {
  children: ReactNode;
  initialTab?: string;
}

function AllTheProviders({ children, initialTab = 'home' }: AllTheProvidersProps) {
  const [currentTab, setCurrentTab] = React.useState(initialTab);

  return (
    <MemoryRouter>
      <TabContext.Provider value={{ currentTab, setCurrentTab }}>
        <ToastProvider>
          {children}
        </ToastProvider>
      </TabContext.Provider>
    </MemoryRouter>
  );
}

const customRender = (
  ui: ReactElement,
  options: CustomRenderOptions = {}
) => {
  const { initialTab, ...renderOptions } = options;

  return render(ui, {
    wrapper: ({ children }) => (
      <AllTheProviders initialTab={initialTab}>
        {children}
      </AllTheProviders>
    ),
    ...renderOptions,
  });
};

// Mock fetch for non-MSW tests
export const mockFetch = (response: any, ok = true, status = 200) => {
  global.fetch = vi.fn(() =>
    Promise.resolve({
      ok,
      status,
      json: () => Promise.resolve(response),
    })
  ) as any;
};

// Mock console methods to avoid noise in tests
export const suppressConsoleError = () => {
  const originalError = console.error;
  beforeEach(() => {
    console.error = vi.fn();
  });
  afterEach(() => {
    console.error = originalError;
  });
};

// Helper to wait for async operations
export const waitForLoadingToFinish = () => {
  return new Promise(resolve => setTimeout(resolve, 0));
};

// Mock window.wpApiSettings for specific tests
export const mockWpApiSettings = (overrides: Partial<typeof window.wpApiSettings> = {}) => {
  Object.defineProperty(window, 'wpApiSettings', {
    value: {
      nonce: 'test-nonce-12345',
      root: 'http://localhost:8888/wp-json/',
      versionString: 'wp/v2/',
      is_whitelabeled: false,
      ...overrides
    },
    writable: true,
    configurable: true
  });
};

// Create a test token object
export const createMockToken = (overrides: Partial<any> = {}) => ({
  id: 1,
  user: {
    id: 1,
    login: 'testuser',
    email: 'test@example.com',
    display_name: 'Test User'
  },
  status: 'active' as const,
  created_at: '2024-01-15T10:30:00Z',
  expires_at: '2024-07-15T10:30:00Z',
  last_used_at: '2024-01-20T14:45:00Z',
