import { describe, it, expect, beforeEach, vi } from 'vitest';
import { analyticsAPI, tokenAPI, settingsAPI, systemAPI } from '@/services/api';

// Focus on testing the API layer which is working well
describe('API Integration Tests', () => {
  beforeEach(() => {
    vi.clearAllMocks();
  });

  describe('Analytics API', () => {
    it('fetches dashboard summary', async () => {
      const result = await analyticsAPI.getDashboardSummary();

      expect(result).toHaveProperty('total_tokens');
      expect(result).toHaveProperty('active_tokens');
      expect(result).toHaveProperty('expired_tokens');
      expect(result).toHaveProperty('revoked_tokens');
      expect(typeof result.total_tokens).toBe('number');
    });

    it('fetches historical data with different periods', async () => {
      const periods = ['7d', '30d', '90d'] as const;

      for (const period of periods) {
        const result = await analyticsAPI.getHistoricalData(period);
        expect(Array.isArray(result)).toBe(true);
        if (result.length > 0) {
          expect(result[0]).toHaveProperty('date');
          expect(result[0]).toHaveProperty('tokens_generated');
        }
      }
    });
  });

  describe('Token API', () => {
    it('lists tokens with pagination', async () => {
      const result = await tokenAPI.list({ page: 1, per_page: 10 });

      expect(result).toHaveProperty('items');
      expect(result).toHaveProperty('pagination');
      expect(Array.isArray(result.items)).toBe(true);
      expect(result.pagination).toHaveProperty('current_page');
      expect(result.pagination).toHaveProperty('total_items');
    });

    it('filters tokens by status', async () => {
      const activeTokens = await tokenAPI.list({ status: 'active' });
      const expiredTokens = await tokenAPI.list({ status: 'expired' });

      // Should get different results for different statuses
      expect(activeTokens.items).not.toEqual(expiredTokens.items);
    });

    it('fetches individual token', async () => {
      const token = await tokenAPI.get(1);

      expect(token).toHaveProperty('id', 1);
      expect(token).toHaveProperty('user');
      expect(token).toHaveProperty('status');
      expect(token.user).toHaveProperty('login');
    });

    it('performs bulk operations', async () => {
      const result = await tokenAPI.bulk({
        action: 'revoke',
        token_ids: [1, 2]
      });

      expect(result).toHaveProperty('success');
      expect(result).toHaveProperty('failed');
      expect(Array.isArray(result.success)).toBe(true);
      expect(Array.isArray(result.failed)).toBe(true);
    });
  });

  describe('Settings API', () => {
    it('fetches current settings', async () => {
      const settings = await settingsAPI.get();

      expect(settings).toHaveProperty('data_management');
      expect(settings).toHaveProperty('token_settings');
      expect(settings).toHaveProperty('user_settings');
      expect(settings).toHaveProperty('rate_limit_settings');
    });

    it('fetches settings options', async () => {
      const options = await settingsAPI.getOptions();

      expect(options).toHaveProperty('analytics_retention');
      expect(options).toHaveProperty('jwt_expiration');
      expect(Array.isArray(options.analytics_retention)).toBe(true);
    });

    it('updates settings', async () => {
      const originalSettings = await settingsAPI.get();
      const updatedSettings = await settingsAPI.update(originalSettings);

      expect(updatedSettings).toEqual(originalSettings);
    });
  });

  describe('System API', () => {
    it('fetches system information', async () => {
      const systemInfo = await systemAPI.getInfo();

      expect(systemInfo).toHaveProperty('wordpress_version');
      expect(systemInfo).toHaveProperty('php_version');
      expect(systemInfo).toHaveProperty('mysql_version');
      expect(systemInfo.wordpress_version).toHaveProperty('version');
      expect(systemInfo.wordpress_version).toHaveProperty('status');
    });
  });

  describe('Error Handling', () => {
    it('handles 404 errors gracefully', async () => {
      await expect(tokenAPI.get(999)).rejects.toThrow();
    });

    it('validates request parameters', async () => {
      // Test with invalid bulk action
      await expect(tokenAPI.bulk({
        action: 'invalid' as any,
        token_ids: [1]
      })).rejects.toThrow();
    });
  });

  describe('Data Consistency', () => {
    it('maintains consistent token structure', async () => {
      const tokenList = await tokenAPI.list({});

      if (tokenList.items.length > 0) {
        const token = tokenList.items[0];
        expect(token).toHaveProperty('id');
        expect(token).toHaveProperty('user');
        expect(token).toHaveProperty('status');
        expect(['active', 'expired', 'revoked']).toContain(token.status);
      }
    });

    it('validates pagination calculations', async () => {
      const result = await tokenAPI.list({ page: 1, per_page: 5 });
      const { pagination } = result;

      expect(pagination.current_page).toBe(1);
      expect(pagination.per_page).toBe(5);
      expect(pagination.total_pages).toBe(
        Math.ceil(pagination.total_items / pagination.per_page)
      );
    });
  });
});

