'use client'

import {
  Disclosure,
  DisclosureButton,
  DisclosurePanel,
} from '@headlessui/react'
import { Bars3Icon } from '@heroicons/react/24/outline'
import { motion } from 'framer-motion'
import { Button } from './button'
import { Link } from './link'
import { Logo } from './logo'

const links = [
  { href: '/#features', label: 'Features' },
  { href: '/#pricing', label: 'Pricing' },
  { href: '/blog', label: 'Blog' },
]

function DesktopNav() {
  return (
    <nav className="hidden md:flex md:gap-x-10">
      {links.map(({ href, label }) => (
        <Link
          key={href}
          href={href}
          className="text-sm font-semibold leading-6 text-gray-900 hover:text-gray-600 transition-colors"
        >
          {label}
        </Link>
      ))}
    </nav>
  )
}

function MobileNavButton() {
  return (
    <DisclosureButton className="flex items-center justify-center rounded-md p-2 text-gray-400 hover:bg-gray-100 hover:text-gray-500 md:hidden">
      <span className="sr-only">Open main menu</span>
      <Bars3Icon className="h-6 w-6" aria-hidden="true" />
    </DisclosureButton>
  )
}

function MobileNav() {
  return (
    <DisclosurePanel className="md:hidden">
      <div className="space-y-1 px-2 pb-3 pt-2 sm:px-3">
        {links.map(({ href, label }, linkIndex) => (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{
              duration: 0.15,
              ease: 'easeInOut',
              delay: linkIndex * 0.1,
            }}
            key={href}
          >
            <Link
              href={href}
              className="block rounded-md px-3 py-2 text-base font-medium text-gray-700 hover:bg-gray-50 hover:text-gray-900"
            >
              {label}
            </Link>
          </motion.div>
        ))}
        <div className="border-t border-gray-200 pt-4">
          <div className="flex flex-col gap-3 px-3">
            <Button
              href="https://docs.jwtauth.pro"
              variant="secondary"
              className="justify-center"
            >
              Documentation
            </Button>
            <Button href="/#pricing" className="justify-center">
              Get Started
            </Button>
          </div>
        </div>
      </div>
    </DisclosurePanel>
  )
}

export function Navbar({ banner }: { banner?: React.ReactNode }) {
  return (
    <Disclosure
      as="header"
      className="fixed left-0 right-0 top-0 z-50 bg-white"
    >
      {/* Vertical line for navbar - matches the site's line */}
      <div className="pointer-events-none absolute bottom-0 left-0 top-0 hidden w-full lg:block">
        <div className="relative mx-auto h-full max-w-2xl px-6 lg:max-w-7xl lg:px-8">
          <div className="absolute -left-3.5 bottom-0 top-0 w-px bg-gray-200 xl:-left-16"></div>
        </div>
      </div>

      <div className="relative mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
        <div className="flex h-20 items-center justify-between">
          <div className="flex items-center space-x-10">
            <Link href="/" className="flex-shrink-0">
              <Logo className="h-9 w-auto" />
            </Link>
            <DesktopNav />
            {banner && <div className="ml-8 hidden md:block">{banner}</div>}
          </div>

          <div className="hidden md:flex md:items-center md:gap-x-3">
            <Button href="https://docs.jwtauth.pro" variant="secondary">
              Documentation
            </Button>
            <Button href="/#pricing">Get Started</Button>
          </div>

          <MobileNavButton />
        </div>
      </div>
      <MobileNav />
    </Disclosure>
  )
}

