import { Container } from '@/components/container'
import { Heading } from '@/components/text'
import { jwtTiers } from '@/data/jwt-tiers'
import { CheckIcon, XMarkIcon } from '@heroicons/react/24/solid'

export function JwtComparison() {
  return (
    <div className="relative py-24">
      <Container>
        <Heading as="h2">Free vs. Pro: Unlock the full potential</Heading>
        <p className="mt-8 max-w-5xl text-balance text-xl/7 text-slate-800 sm:text-2xl/8">
          Already using our free plugin? Discover how the Pro version enhances
          your API security with advanced features, comprehensive monitoring,
          and professional-grade controls to safeguard your WordPress site.
        </p>
        <div className="mt-16 overflow-hidden border border-gray-100 bg-white">
          <table className="min-w-full divide-y divide-gray-200">
            <thead>
              <tr className="bg-gray-50">
                <th scope="col" className="sr-only">
                  Feature
                </th>
                {jwtTiers.map((tier) => (
                  <th
                    key={tier.slug}
                    scope="col"
                    className="px-6 py-6 text-center text-sm font-semibold text-gray-900"
                  >
                    {tier.name}
                    <span className="mt-1 block text-sm font-normal italic text-gray-500">
                      {typeof tier.priceMonthly === 'number'
                        ? `starts at $${tier.priceMonthly}/yr`
                        : tier.priceMonthly}
                    </span>
                  </th>
                ))}
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {jwtTiers[0].features.map((feature, featureIdx) => (
                <tr
                  key={feature.name}
                  className={featureIdx % 2 === 0 ? 'bg-white' : 'bg-gray-50'}
                >
                  <td className="py-4 pl-6 text-sm font-medium text-gray-900">
                    {feature.name}
                  </td>
