Cool, now I want to add a new component (features table) in the @src/components/sections/features/ section (at the end) based on the code below, the features should be taken from @FEATURES.md file of course copying, parsing and format the data to be usable, the code is "use client"

import { useState } from "react"
import { Button } from "@/components/ui/button"
import { ChevronDown, ChevronUp } from "lucide-react"
import { featuresData, categories } from "../data/features"

export function FeaturesTable() {
  const [showAll, setShowAll] = useState(false)
  const [isAnimating, setIsAnimating] = useState(false)

  // Group features by category
  const groupedFeatures = categories.reduce(
    (acc, category) => {
      const categoryFeatures = featuresData.filter((f) => f.category === category)
      if (categoryFeatures.length > 0) {
        acc[category] = categoryFeatures
      }
      return acc
    },
    {} as Record<string, typeof featuresData>,
  )

  // Create a flat array of all rows (including category separators) for counting
  const allRows: Array<{ type: "category" | "feature"; category?: string; feature?: any }> = []
  Object.entries(groupedFeatures).forEach(([category, features]) => {
    allRows.push({ type: "category", category })
    features.forEach((feature) => {
      allRows.push({ type: "feature", feature })
    })
  })

  const visibleRows = showAll ? allRows : allRows.slice(0, 10)
  const hasMoreRows = allRows.length > 10

  const handleToggleShowAll = () => {
    setIsAnimating(true)
    setShowAll(!showAll)

    // Reset animation state after animation completes
    setTimeout(() => {
      setIsAnimating(false)
    }, 600)
  }

  return (
    <div className="bg-white">
      {/* Table Container - positioned relative for gradient */}
      <div className="relative mb-6">
        <div className="bg-white border border-gray-200 rounded-lg overflow-hidden shadow-sm">
          <div className="overflow-x-auto">
            <table className="w-full">
              <tbody>
                {visibleRows.map((row, index) => {
                  // Determine if this row should be animated (only new rows when expanding)
                  const shouldAnimate = isAnimating && showAll && index >= 10

                  if (row.type === "category") {
                    return (
                      <tr
                        key={`category-${row.category}`}
                        className={`bg-gray-100 ${
                          shouldAnimate ? "animate-in fade-in slide-in-from-top-2 duration-300" : ""
                        }`}
                        style={shouldAnimate ? { animationDelay: `${(index - 10) * 50}ms` } : {}}
                      >
                        <td colSpan={2} className="py-4 px-8 font-semibold text-gray-900 border-b border-gray-200">
                          {row.category}
                        </td>
                      </tr>
                    )
                  } else {
                    const feature = row.feature!
                    return (
                      <tr
                        key={feature.id}
                        className={`hover:bg-gray-50 transition-colors duration-150 border-b border-gray-200 ${
                          shouldAnimate ? "animate-in fade-in slide-in-from-top-2 duration-300" : ""
                        }`}
                        style={shouldAnimate ? { animationDelay: `${(index - 10) * 50}ms` } : {}}
                      >
                        <td className="py-6 px-8">
                          <div className="font-medium text-gray-900 leading-tight">{feature.name}</div>
                        </td>
                        <td className="py-6 px-8">
                          <div className="text-gray-700 leading-relaxed">{feature.description}</div>
                        </td>
                      </tr>
                    )
                  }
                })}
              </tbody>
            </table>
          </div>
        </div>

        {/* Gradient Overlay - smaller to cover just the last 2 rows */}
        {!showAll && hasMoreRows && (
          <div className="absolute bottom-0 left-0 right-0 h-32 bg-gradient-to-t from-white via-white/90 to-transparent pointer-events-none rounded-b-lg transition-opacity duration-500 ease-in-out" />
        )}
      </div>

      {/* See More/Less Button - outside the table container */}
      {hasMoreRows && (
        <div className="flex justify-center">
          <Button
            onClick={handleToggleShowAll}
            variant="outline"
            className="flex items-center gap-2 px-6 py-3 border-gray-300 hover:border-red-500 hover:text-red-600 transition-all duration-300 hover:shadow-md transform hover:scale-105"
          >
            <div className={`transition-transform duration-300 ${showAll ? "rotate-180" : "rotate-0"}`}>
              {showAll ? <ChevronUp className="w-4 h-4" /> : <ChevronDown className="w-4 h-4" />}
            </div>
            <span className="transition-all duration-200">
              {showAll ? "show less" : <>see more ({allRows.length - 10} more)</>}
            </span>
          </Button>
        </div>
      )}
    </div>
  )
}

