import { clsx } from 'clsx'
import { Link } from './link'

const variants = {
  primary: clsx(
    'inline-flex items-center justify-center px-6 py-3',
    'rounded-lg border border-transparent bg-primary shadow-sm',
    'whitespace-nowrap text-sm font-semibold text-white',
    'hover:bg-primary/80 disabled:bg-primary/80 disabled:opacity-40',
    'transition-colors duration-200',
  ),
  secondary: clsx(
    'inline-flex items-center justify-center px-6 py-3',
    'rounded-lg border border-gray-300 bg-white shadow-sm',
    'whitespace-nowrap text-sm font-semibold text-gray-900',
    'hover:bg-gray-50 disabled:bg-gray-50 disabled:opacity-40',
    'transition-colors duration-200',
  ),
  outline: clsx(
    'inline-flex items-center justify-center px-4 py-2',
    'rounded-lg border border-gray-300 bg-white shadow-sm',
    'whitespace-nowrap text-sm font-medium text-gray-700',
    'hover:bg-gray-50 disabled:bg-gray-50 disabled:opacity-40',
    'transition-colors duration-200',
  ),
}

type ButtonProps = {
  variant?: keyof typeof variants
} & (
  | React.ComponentPropsWithoutRef<typeof Link>
  | (React.ButtonHTMLAttributes<HTMLButtonElement> & { href?: undefined })
)

export function Button({
  variant = 'primary',
  className,
  ...props
}: ButtonProps) {
  className = clsx(className, variants[variant as keyof typeof variants])

  if (typeof props.href === 'undefined') {
    return <button {...props} className={className} />
  }

  return <Link {...props} className={className} />
}

